                emergencyStop: true
              }
            },
            {
              id: 'farm-2',
              name: 'Arbitrage Hunters',
              description: 'Cross-exchange arbitrage opportunities',
              agents: ['agent-4', 'agent-5'],
              strategy: 'arbitrage',
              status: 'active',
