              pnl24h: farm.pnl24h + (Math.random() - 0.5) * 100,
              pnlPercent: farm.pnlPercent + (Math.random() - 0.5) * 0.5
            };
            await get().updateFarm(id, updatedFarm);
          }
        } catch (error) {
          console.error('Refresh farm data error:', error);
        }
      }
    }),
    {
      name: 'farm-store',
      partialize: (state) => ({ 
        farms: state.farms,
        selectedFarm: state.selectedFarm 
      })
    }
  )
);

