/**
 * Python Trading Libraries Integration
 * Interfaces for connecting to Python-based trading libraries and services
 */

export interface TradingLibraryConfig {
  library: 'pandas' | 'numpy' | 'sklearn' | 'backtrader' | 'zipline' | 'quantlib' | 'ta-lib' | 'ccxt';
  version: string;
  enabled: boolean;
  config: Record<string, any>;
}

export interface TechnicalIndicator {
  name: string;
  type: 'momentum' | 'trend' | 'volatility' | 'volume' | 'oscillator';
  parameters: Record<string, number>;
  values: number[];
  signal: 'buy' | 'sell' | 'hold';
  confidence: number;
}

export interface BacktestResult {
  strategy_name: string;
  start_date: string;
  end_date: string;
  initial_capital: number;
  final_portfolio_value: number;
  total_return: number;
  annualized_return: number;
  volatility: number;
  sharpe_ratio: number;
  max_drawdown: number;
  win_rate: number;
  profit_factor: number;
  total_trades: number;
  winning_trades: number;
  losing_trades: number;
  avg_trade_return: number;
  trades: BacktestTrade[];
}

export interface BacktestTrade {
  entry_date: string;
  exit_date: string;
  symbol: string;
  side: 'long' | 'short';
  entry_price: number;
  exit_price: number;
  quantity: number;
  pnl: number;
  pnl_percent: number;
  duration_days: number;
}

export interface StrategyParameters {
  name: string;
  description: string;
  category: 'momentum' | 'mean_reversion' | 'breakout' | 'arbitrage' | 'market_making';
  parameters: {
    [key: string]: {
      value: number | string | boolean;
      type: 'number' | 'string' | 'boolean' | 'range';
      min?: number;
      max?: number;
      options?: string[];
      description: string;
    };
  };
  risk_management: {
    max_position_size: number;
    stop_loss_percent: number;
    take_profit_percent: number;
    max_daily_loss: number;
  };
}

export interface PythonTradingService {
  // Technical Analysis
  calculateIndicators(symbol: string, timeframe: string, indicators: string[]): Promise<TechnicalIndicator[]>;

  // Strategy Testing
  backtestStrategy(
    strategy: StrategyParameters,
    symbols: string[],
    start_date: string,
    end_date: string,
    initial_capital: number
  ): Promise<BacktestResult>;

  // Real-time Analysis
  analyzeMarket(symbols: string[]): Promise<{
    symbol: string;
    price: number;
    indicators: TechnicalIndicator[];
    signals: {
      overall: 'bullish' | 'bearish' | 'neutral';
      strength: number;
      timeframes: {
        '1h': 'bullish' | 'bearish' | 'neutral';
        '4h': 'bullish' | 'bearish' | 'neutral';
        '1d': 'bullish' | 'bearish' | 'neutral';
      };
    };
  }[]>;

  // Portfolio Optimization
  optimizePortfolio(
    symbols: string[],
    timeframe: string,
    method: 'mean_variance' | 'risk_parity' | 'maximum_sharpe' | 'minimum_volatility'
  ): Promise<{
    weights: { [symbol: string]: number };
    expected_return: number;
    expected_volatility: number;
    sharpe_ratio: number;
    metrics: {
      var_95: number;
      cvar_95: number;
      max_drawdown: number;
    };
  }>;

  // Machine Learning Predictions
  predictPrice(
    symbol: string,
    model_type: 'lstm' | 'random_forest' | 'svm' | 'xgboost',
    timeframe: string,
    prediction_horizon: number
  ): Promise<{
    symbol: string;
    current_price: number;
    predicted_prices: number[];
    confidence_interval: { lower: number; upper: number }[];
    model_accuracy: number;
    feature_importance: { [feature: string]: number };
  }>;

  // Risk Management
  calculateRiskMetrics(
    portfolio: { symbol: string; quantity: number; price: number }[],
    timeframe: string
  ): Promise<{
    var_1d: number;
    var_1w: number;
    expected_shortfall: number;
    beta: number;
    correlation_matrix: { [key: string]: { [key: string]: number } };
    stress_test_scenarios: {
      scenario: string;
      portfolio_change: number;
      probability: number;
    }[];
  }>;
}

