/**
 * Real-Time WebSocket Service
 * Handles live data streaming for market data, agent status, and system events
 */

import { EventEmitter } from 'events';

export interface RealTimeEvent {
  type: 'market_data' | 'agent_status' | 'portfolio_update' | 'trade_execution' | 'system_alert' | 'defi_update';
  data: any;
  timestamp: string;
  source: string;
}

export interface WebSocketConfig {
  url: string;
  reconnectInterval: number;
  maxReconnectAttempts: number;
  heartbeatInterval: number;
  subscriptions: string[];
}

export interface MarketDataUpdate {
  symbol: string;
  price: number;
  change: number;
  volume: number;
  timestamp: string;
}

export interface AgentStatusUpdate {
  agentId: string;
  status: 'active' | 'inactive' | 'error' | 'paused';
  balance: number;
  pnl: number;
  lastAction: string;
  timestamp: string;
}

export interface PortfolioUpdate {
  totalValue: number;
  totalPnL: number;
  positions: {
    symbol: string;
    quantity: number;
    value: number;
    pnl: number;
  }[];
  timestamp: string;
}

export interface TradeExecutionUpdate {
  tradeId: string;
  agentId: string;
  symbol: string;
  side: 'buy' | 'sell';
  quantity: number;
  price: number;
  status: 'pending' | 'filled' | 'cancelled' | 'failed';
  timestamp: string;
}

export interface SystemAlert {
  id: string;
  level: 'info' | 'warning' | 'error' | 'critical';
  message: string;
  component: string;
  timestamp: string;
  acknowledged: boolean;
}

class RealTimeService extends EventEmitter {
  private ws: WebSocket | null = null;
  private config: WebSocketConfig;
  private reconnectAttempts = 0;
  private heartbeatTimer: NodeJS.Timeout | null = null;
  private reconnectTimer: NodeJS.Timeout | null = null;
  private isConnecting = false;
  private subscriptions = new Set<string>();

  constructor(config: Partial<WebSocketConfig> = {}) {
    super();

    this.config = {
      url: process.env.NEXT_PUBLIC_WS_URL || 'ws://localhost:8000/ws',
      reconnectInterval: 5000,
      maxReconnectAttempts: 10,
      heartbeatInterval: 30000,
      subscriptions: ['market_data', 'agent_status', 'portfolio_updates'],
      ...config
    };
  }

  async connect(): Promise<void> {
    if (this.isConnecting || this.isConnected()) {
      return;
    }

    this.isConnecting = true;

    try {
      this.ws = new WebSocket(this.config.url);

      this.ws.onopen = this.handleOpen.bind(this);
      this.ws.onmessage = this.handleMessage.bind(this);
      this.ws.onclose = this.handleClose.bind(this);
      this.ws.onerror = this.handleError.bind(this);

      // Wait for connection or timeout
      await new Promise((resolve, reject) => {
        const timeout = setTimeout(() => {
          reject(new Error('WebSocket connection timeout'));
        }, 10000);

        this.ws!.onopen = () => {
          clearTimeout(timeout);
          this.handleOpen();
          resolve(void 0);
        };

        this.ws!.onerror = (error) => {
          clearTimeout(timeout);
          reject(error);
        };
      });

    } catch (error) {
      this.isConnecting = false;
      console.error('WebSocket connection failed:', error);
      this.scheduleReconnect();
      throw error;
    }
  }

  disconnect(): void {
    if (this.heartbeatTimer) {
      clearInterval(this.heartbeatTimer);
      this.heartbeatTimer = null;
    }

    if (this.reconnectTimer) {
      clearTimeout(this.reconnectTimer);
      this.reconnectTimer = null;
    }

    if (this.ws) {
      this.ws.close();
      this.ws = null;
    }

