  1	/**
  2	 * Authentication Service for Cival Trading Platform
  3	 * Handles JWT tokens, session management, and Supabase Auth integration
  4	 */
  5	
  6	import { useState, useEffect } from 'react'
  7	import { createBrowserClient } from '@/utils/supabase/client'
  8	import { User, Session } from '@supabase/supabase-js'
  9	import { Database } from '@/types/database.types'
 10	
 11	export interface AuthUser {
 12	  id: string
 13	  email?: string
 14	  name?: string
 15	  role?: 'trader' | 'admin' | 'viewer'
 16	  permissions?: string[]
 17	  trading_enabled?: boolean
 18	  created_at?: string
 19	}
 20	
 21	export interface AuthSession {
 22	  user: AuthUser
 23	  access_token: string
 24	  refresh_token?: string
 25	  expires_at: number
 26	}
 27	
 28	class AuthenticationService {
 29	  private supabase = createBrowserClient()
 30	  private currentSession: AuthSession | null = null
 31	  private sessionListeners: ((session: AuthSession | null) => void)[] = []
 32	
 33	  constructor() {
 34	    this.initializeAuth()
 35	  }
 36	
 37	  /**
 38	   * Initialize authentication state
 39	   */
 40	  private async initializeAuth() {
 41	    try {
 42	      // Check for existing session
 43	      const { data: { session }, error } = await this.supabase.auth.getSession()
 44	      
 45	      if (error) {
 46	        console.error('Error getting session:', error)
 47	        return
 48	      }
 49	
 50	      if (session) {
 51	        this.currentSession = this.transformSession(session)
 52	        this.notifySessionListeners()
 53	      }
 54	
 55	      // Listen for auth changes
 56	      this.supabase.auth.onAuthStateChange((event, session) => {
 57	        console.log('Auth state changed:', event)
 58	        
 59	        if (session) {
 60	          this.currentSession = this.transformSession(session)
 61	        } else {
 62	          this.currentSession = null
 63	        }
 64	        
 65	        this.notifySessionListeners()
 66	      })
 67	
 68	    } catch (error) {
 69	      console.error('Failed to initialize auth:', error)
 70	    }
 71	  }
 72	
 73	  /**
 74	   * Transform Supabase session to our AuthSession format
 75	   */
 76	  private transformSession(session: Session): AuthSession {
 77	    const user: AuthUser = {
 78	      id: session.user.id,
 79	      email: session.user.email,
 80	      name: session.user.user_metadata?.name || session.user.email,
 81	      role: session.user.user_metadata?.role || 'trader',
 82	      permissions: session.user.user_metadata?.permissions || ['read', 'trade'],
 83	      trading_enabled: session.user.user_metadata?.trading_enabled || true,
 84	      created_at: session.user.created_at
 85	    }
 86	
 87	    return {
 88	      user,
 89	      access_token: session.access_token,
 90	      refresh_token: session.refresh_token,
 91	      expires_at: session.expires_at || Date.now() / 1000 + 3600
 92	    }
 93	  }
 94	
 95	  /**
 96	   * Sign in with email and password
 97	   */
 98	  async signIn(email: string, password: string): Promise<{ success: boolean; error?: string }> {
 99	    try {
100	      const { data, error } = await this.supabase.auth.signInWithPassword({
101	        email,
102	        password
103	      })
104	
105	      if (error) {
106	        return { success: false, error: error.message }
107	      }
108	
109	      if (data.session) {
110	        this.currentSession = this.transformSession(data.session)
111	        this.notifySessionListeners()
112	      }
113	
114	      return { success: true }
115	    } catch (error) {
116	      return { 
117	        success: false, 
118	        error: error instanceof Error ? error.message : 'Sign in failed' 
119	      }
120	    }
121	  }
122	
123	  /**
124	   * Sign up with email and password
125	   */
126	  async signUp(email: string, password: string, metadata?: any): Promise<{ success: boolean; error?: string }> {
127	    try {
128	      const { data, error } = await this.supabase.auth.signUp({
129	        email,
130	        password,
131	        options: {
132	          data: {
133	            role: 'trader',
134	            trading_enabled: true,
135	            permissions: ['read', 'trade'],
136	            ...metadata
137	          }
138	        }
139	      })
140	
141	      if (error) {
142	        return { success: false, error: error.message }
143	      }
144	
145	      return { success: true }
146	    } catch (error) {
147	      return { 
148	        success: false, 
149	        error: error instanceof Error ? error.message : 'Sign up failed' 
150	      }
151	    }
152	  }
153	
154	  /**
155	   * Sign out current user
156	   */
157	  async signOut(): Promise<{ success: boolean; error?: string }> {
158	    try {
159	      const { error } = await this.supabase.auth.signOut()
160	      
161	      if (error) {
162	        return { success: false, error: error.message }
163	      }
164	
165	      this.currentSession = null
166	      this.notifySessionListeners()
167	      
168	      return { success: true }
169	    } catch (error) {
170	      return { 
171	        success: false, 
172	        error: error instanceof Error ? error.message : 'Sign out failed' 
173	      }
174	    }
175	  }
176	
177	  /**
178	   * Get current session
179	   */
180	  getSession(): AuthSession | null {
181	    return this.currentSession
182	  }
183	
184	  /**
185	   * Get current user
186	   */
187	  getCurrentUser(): AuthUser | null {
188	    return this.currentSession?.user || null
189	  }
190	
191	  /**
192	   * Check if user is authenticated
193	   */
194	  isAuthenticated(): boolean {
195	    if (!this.currentSession) return false
196	    
197	    // Check if token is expired
198	    const now = Date.now() / 1000
199	    return this.currentSession.expires_at > now
200	  }
201	
202	  /**
203	   * Get access token for API calls
204	   */
205	  getAccessToken(): string | null {
206	    if (!this.isAuthenticated()) return null
207	    return this.currentSession?.access_token || null
208	  }
209	
210	  /**
211	   * Refresh the current session
212	   */
213	  async refreshSession(): Promise<{ success: boolean; error?: string }> {
214	    try {
215	      const { data, error } = await this.supabase.auth.refreshSession()
216	      
217	      if (error) {
218	        return { success: false, error: error.message }
219	      }
220	
221	      if (data.session) {
222	        this.currentSession = this.transformSession(data.session)
223	        this.notifySessionListeners()
224	      }
225	
226	      return { success: true }
227	    } catch (error) {
228	      return { 
229	        success: false, 
230	        error: error instanceof Error ? error.message : 'Session refresh failed' 
231	      }
232	    }
233	  }
234	
235	  /**
236	   * Check if user has specific permission
237	   */
238	  hasPermission(permission: string): boolean {
239	    const user = this.getCurrentUser()
240	    if (!user) return false
241	    
242	    return user.permissions?.includes(permission) || user.role === 'admin'
243	  }
244	
245	  /**
246	   * Check if user can trade
247	   */
248	  canTrade(): boolean {
249	    const user = this.getCurrentUser()
250	    if (!user) return false
251	    
252	    return user.trading_enabled === true && this.hasPermission('trade')
253	  }
254	
255	  /**
256	   * Add session change listener
257	   */
258	  onSessionChange(callback: (session: AuthSession | null) => void): () => void {
259	    this.sessionListeners.push(callback)
260	    
261	    // Return unsubscribe function
262	    return () => {
263	      const index = this.sessionListeners.indexOf(callback)
264	      if (index > -1) {
265	        this.sessionListeners.splice(index, 1)
266	      }
267	    }
268	  }
269	
270	  /**
271	   * Notify all session listeners
272	   */
273	  private notifySessionListeners() {
274	    this.sessionListeners.forEach(listener => {
275	      try {
276	        listener(this.currentSession)
277	      } catch (error) {
278	        console.error('Error in session listener:', error)
279	      }
280	    })
281	  }
282	
283	  /**
284	   * Get authorization header for API calls
285	   */
286	  getAuthHeader(): Record<string, string> {
287	    const token = this.getAccessToken()
288	    if (!token) return {}
289	    
290	    return {
291	      'Authorization': `Bearer ${token}`
292	    }
293	  }
294	
295	  /**
296	   * Create a mock session for development
297	   */
298	  createMockSession(): AuthSession {
299	    const mockUser: AuthUser = {
300	      id: 'mock-user-' + Date.now(),
301	      email: 'trader@cival.ai',
302	      name: 'Mock Trader',
303	      role: 'trader',
304	      permissions: ['read', 'trade', 'analyze'],
305	      trading_enabled: true,
306	      created_at: new Date().toISOString()
307	    }
308	
309	    return {
310	      user: mockUser,
311	      access_token: 'mock-jwt-token-' + Date.now(),
312	      expires_at: Date.now() / 1000 + 86400 // 24 hours
313	    }
314	  }
315	
316	  /**
317	   * Enable mock mode for development
318	   */
319	  enableMockMode() {
320	    console.log('ðŸ”§ Auth: Mock mode enabled for development')
321	    this.currentSession = this.createMockSession()
322	    this.notifySessionListeners()
323	  }
324	
325	  /**
326	   * Disable mock mode
327	   */
328	  disableMockMode() {
329	    console.log('ðŸ”§ Auth: Mock mode disabled')
330	    this.currentSession = null
331	    this.notifySessionListeners()
332	  }
333	}
334	
335	// Create singleton instance
336	export const authService = new AuthenticationService()
337	
338	// Export hooks for React components
339	export function useAuth() {
340	  const [session, setSession] = useState<AuthSession | null>(authService.getSession())
341	  const [loading, setLoading] = useState(true)
342	
343	  useEffect(() => {
344	    // Set initial state
345	    setSession(authService.getSession())
346	    setLoading(false)
347	
348	    // Subscribe to session changes
349	    const unsubscribe = authService.onSessionChange((newSession) => {
350	      setSession(newSession)
351	      setLoading(false)
352	    })
353	
354	    return unsubscribe
355	  }, [])
356	
357	  return {
358	    session,
359	    user: session?.user || null,
360	    loading,
361	    isAuthenticated: authService.isAuthenticated(),
362	    canTrade: authService.canTrade(),
363	    hasPermission: (permission: string) => authService.hasPermission(permission),
364	    signIn: authService.signIn.bind(authService),
