"""
Redis Integration for Agent Zero
Provides fast caching, session management, and real-time data storage
"""

import asyncio
import json
import pickle
from typing import Dict, List, Optional, Any, Union
from datetime import datetime, timedelta
import os

try:
    import redis.asyncio as aioredis
    from redis.asyncio import Redis as AsyncRedis
    REDIS_AVAILABLE = True
except ImportError:
    REDIS_AVAILABLE = False

from python.helpers.print_style import PrintStyle
import python.helpers.log as Log


class RedisConfig:
    """Configuration for Redis integration"""

    def __init__(self):
        self.host = os.getenv("REDIS_HOST", "localhost")
        self.port = int(os.getenv("REDIS_PORT", "6379"))
        self.password = os.getenv("REDIS_PASSWORD")
        self.db = int(os.getenv("REDIS_DB", "0"))
        self.url = os.getenv("REDIS_URL")
        self.max_connections = int(os.getenv("REDIS_MAX_CONNECTIONS", "10"))
        self.socket_timeout = int(os.getenv("REDIS_SOCKET_TIMEOUT", "5"))

        # Agent Zero specific prefixes
        self.agent_prefix = "agent_zero:agent:"
        self.context_prefix = "agent_zero:context:"
        self.session_prefix = "agent_zero:session:"
        self.cache_prefix = "agent_zero:cache:"
        self.pubsub_prefix = "agent_zero:pubsub:"


class RedisClient:
    """Redis client wrapper for Agent Zero"""

    def __init__(self, config: RedisConfig):
        self.config = config
        self.client: Optional[AsyncRedis] = None
        self.pubsub = None
        self.is_connected = False

    async def connect(self):
        """Connect to Redis"""
        if not REDIS_AVAILABLE:
            PrintStyle(font_color="yellow", padding=True).print("Redis not available - install with: pip install redis")
            return False

        try:
            if self.config.url:
                # Use Redis URL
                self.client = aioredis.from_url(
                    self.config.url,
                    socket_timeout=self.config.socket_timeout,
                    max_connections=self.config.max_connections
                )
            else:
                # Use individual parameters
                self.client = aioredis.Redis(
                    host=self.config.host,
                    port=self.config.port,
                    password=self.config.password,
                    db=self.config.db,
                    socket_timeout=self.config.socket_timeout,
                    max_connections=self.config.max_connections
                )

            # Test connection
            await self.client.ping()
            self.is_connected = True

            PrintStyle(font_color="green", padding=True).print("Redis connection established")
            return True

        except Exception as e:
            PrintStyle(font_color="red", padding=True).print(f"Redis connection failed: {e}")
            return False

    async def disconnect(self):
        """Disconnect from Redis"""
        if self.client:
            await self.client.close()
            self.is_connected = False

    def is_available(self) -> bool:
        """Check if Redis is available and connected"""
        return REDIS_AVAILABLE and self.is_connected and self.client is not None

    # Basic operations
    async def set(self, key: str, value: Any, expire: Optional[int] = None) -> bool:
        """Set a value in Redis"""
        if not self.is_available():
            return False

        try:
            # Serialize complex objects
            if isinstance(value, (dict, list, tuple)):
                value = json.dumps(value)
            elif not isinstance(value, (str, int, float, bytes)):
                value = pickle.dumps(value)

            await self.client.set(key, value, ex=expire)
            return True

        except Exception as e:
            PrintStyle(font_color="red", padding=True).print(f"Redis SET error: {e}")
            return False

    async def get(self, key: str) -> Optional[Any]:
        """Get a value from Redis"""
        if not self.is_available():
            return None

        try:
            value = await self.client.get(key)
            if value is None:
                return None

            # Try to deserialize
            try:
                # Try JSON first
                return json.loads(value)
            except (json.JSONDecodeError, TypeError):
                try:
                    # Try pickle
                    return pickle.loads(value)
                except (pickle.PickleError, TypeError):
                    # Return as string
                    return value.decode() if isinstance(value, bytes) else value

        except Exception as e:
            PrintStyle(font_color="red", padding=True).print(f"Redis GET error: {e}")
            return None

    async def delete(self, key: str) -> bool:
        """Delete a key from Redis"""
        if not self.is_available():
            return False

        try:
            result = await self.client.delete(key)
            return result > 0
        except Exception as e:
            PrintStyle(font_color="red", padding=True).print(f"Redis DELETE error: {e}")
            return False

    async def exists(self, key: str) -> bool:
        """Check if a key exists in Redis"""
        if not self.is_available():
            return False

        try:
            result = await self.client.exists(key)
            return result > 0
        except Exception as e:
            PrintStyle(font_color="red", padding=True).print(f"Redis EXISTS error: {e}")
            return False

    # Hash operations
    async def hset(self, name: str, mapping: Dict[str, Any]) -> bool:
        """Set hash fields"""
        if not self.is_available():
            return False

        try:
            # Serialize values
            serialized_mapping = {}
            for k, v in mapping.items():
                if isinstance(v, (dict, list, tuple)):
                    serialized_mapping[k] = json.dumps(v)
                else:
                    serialized_mapping[k] = str(v)

            await self.client.hset(name, mapping=serialized_mapping)
            return True
        except Exception as e:
