"""
Trading System Tool for Agent Zero
Integrates with the enhanced trading dashboard and provides trading capabilities
"""

import asyncio
import json
from typing import Dict, List, Optional, Any
from datetime import datetime

from python.helpers.tool import Tool, Response
from python.helpers.print_style import PrintStyle
import python.helpers.log as Log

# Import our enhanced integrations
from python.helpers.autogen_integration import get_autogen_integration
from python.helpers.letta_memory import get_memory_integration
from python.helpers.redis_integration import get_redis_manager
from python.helpers.supabase_integration import get_supabase_manager

try:
    import httpx
    HTTPX_AVAILABLE = True
except ImportError:
    HTTPX_AVAILABLE = False

try:
    from hyperliquid.info import Info
    from hyperliquid.exchange import Exchange
    from hyperliquid.utils import constants
    HYPERLIQUID_AVAILABLE = True
except ImportError:
    HYPERLIQUID_AVAILABLE = False


class TradingSystem(Tool):
    """Enhanced trading system tool for Agent Zero"""

    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.trading_api_base = "http://localhost:8002"  # Default trading API base
        self.autogen = None
        self.memory = None
        self.redis = None
        self.supabase = None
        self._initialize_integrations()

    def _initialize_integrations(self):
        """Initialize all integrations"""
        try:
            # Initialize AutoGen integration
            self.autogen = get_autogen_integration(self.agent.context)

            # Initialize memory integration
            self.memory = get_memory_integration(self.agent.context)

            # Initialize Redis integration
            self.redis = get_redis_manager(self.agent.context)
            asyncio.create_task(self.redis.initialize())

            # Initialize Supabase integration
            self.supabase = get_supabase_manager(self.agent.context)
            self.supabase.initialize()

            PrintStyle(font_color="green", padding=True).print("Trading system integrations initialized")

        except Exception as e:
            PrintStyle(font_color="red", padding=True).print(f"Trading system initialization error: {e}")

    async def execute(self, **kwargs):
        """Main execution method for trading operations"""
        action = kwargs.get("action", "")

        if not action:
            return Response(
                message="Trading system requires an action parameter. Available actions: analyze, trade, portfolio, market_data, create_agent, run_strategy",
                break_loop=False
            )

        try:
            if action == "analyze":
                result = await self._analyze_market(kwargs)
            elif action == "trade":
                result = await self._execute_trade(kwargs)
            elif action == "portfolio":
                result = await self._get_portfolio_status(kwargs)
            elif action == "market_data":
                result = await self._get_market_data(kwargs)
            elif action == "create_agent":
                result = await self._create_trading_agent(kwargs)
            elif action == "run_strategy":
                result = await self._run_trading_strategy(kwargs)
            elif action == "memory_recall":
                result = await self._recall_trading_memories(kwargs)
            elif action == "store_decision":
                result = await self._store_trading_decision(kwargs)
            else:
                result = {
                    "error": f"Unknown action: {action}",
                    "available_actions": ["analyze", "trade", "portfolio", "market_data", "create_agent", "run_strategy", "memory_recall", "store_decision"]
                }

            # Store the result in memory if successful
            if not result.get("error") and self.memory and self.memory.is_available():
                await self.memory.store_agent_memory(
                    self.agent,
                    f"Trading action '{action}' executed: {json.dumps(result)[:500]}",
                    memory_type="procedural",
                    context={"action": action, "timestamp": datetime.now().isoformat()},
                    importance=0.7
                )

            return Response(message=json.dumps(result, indent=2), break_loop=False)

        except Exception as e:
            error_msg = f"Trading system error: {str(e)}"
            PrintStyle(font_color="red", padding=True).print(error_msg)
            return Response(message=error_msg, break_loop=False)

    async def _analyze_market(self, kwargs) -> Dict[str, Any]:
        """Analyze market using AutoGen multi-agent system"""
        symbol = kwargs.get("symbol", "BTC")
        timeframe = kwargs.get("timeframe", "1h")

        try:
            # Get market data
            market_data = await self._fetch_market_data(symbol, timeframe)

            if not market_data:
                return {"error": "Failed to fetch market data"}

            # Use AutoGen for multi-agent analysis if available
            if self.autogen and self.autogen.is_available():
                analysis = await self.autogen.run_trading_analysis(market_data)

                # Store analysis in Supabase
                if self.supabase and self.supabase.is_available():
                    await self.supabase.store_trading_decision(self.agent, {
                        "action": "analysis",
                        "symbol": symbol,
                        "analysis": analysis,
                        "market_data": market_data
                    })

                return {
                    "symbol": symbol,
                    "timeframe": timeframe,
                    "analysis": analysis,
                    "market_data": market_data,
                    "timestamp": datetime.now().isoformat()
                }
            else:
                # Fallback to simple analysis
                return {
                    "symbol": symbol,
                    "timeframe": timeframe,
                    "market_data": market_data,
                    "analysis": "AutoGen not available - basic analysis performed",
                    "timestamp": datetime.now().isoformat()
                }

