                    if symbol in all_mids:
                        price = all_mids[symbol]

                        return {
                            "symbol": symbol,
                            "price": float(price),
                            "timeframe": timeframe,
                            "source": "hyperliquid",
                            "timestamp": datetime.now().isoformat()
                        }
                except Exception as e:
                    PrintStyle(font_color="yellow", padding=True).print(f"Hyperliquid fetch failed: {e}")

            # Try trading API
            if HTTPX_AVAILABLE:
                try:
                    async with httpx.AsyncClient() as client:
                        response = await client.get(
                            f"{self.trading_api_base}/api/v1/market_data/{symbol}",
                            params={"timeframe": timeframe}
                        )
                        if response.status_code == 200:
                            return response.json()
                except Exception as e:
                    PrintStyle(font_color="yellow", padding=True).print(f"Trading API fetch failed: {e}")

            # Fallback to mock data
            return {
                "symbol": symbol,
                "price": 50000.0 if symbol == "BTC" else 3000.0,
                "timeframe": timeframe,
                "volume": 1000000,
                "change_24h": 2.5,
                "source": "mock",
                "timestamp": datetime.now().isoformat()
            }

        except Exception as e:
            PrintStyle(font_color="red", padding=True).print(f"Market data fetch error: {e}")
            return None

