                            "symbol": {"type": "string", "description": "Trading symbol"},
                            "agent_team": {"type": "string", "description": "Agent team to use"},
                            "parameters": {"type": "object", "description": "Strategy parameters"}
                        },
                        "required": ["strategy", "symbol"]
                    }
                )
            ]

        @self.server.call_tool()
        async def handle_call_tool(name: str, arguments: Dict[str, Any]) -> Sequence[TextContent | ImageContent]:
            """Handle tool calls"""
            try:
                if name == "trading_analysis":
                    result = await self._handle_trading_analysis(arguments)
                elif name == "execute_trade":
                    result = await self._handle_execute_trade(arguments)
                elif name == "portfolio_status":
                    result = await self._handle_portfolio_status(arguments)
                elif name == "market_data":
                    result = await self._handle_market_data(arguments)
                elif name == "create_trading_agent":
                    result = await self._handle_create_trading_agent(arguments)
                elif name == "agent_memory_search":
                    result = await self._handle_memory_search(arguments)
                elif name == "store_trading_decision":
                    result = await self._handle_store_decision(arguments)
                elif name == "run_trading_strategy":
                    result = await self._handle_run_strategy(arguments)
                else:
                    result = {"error": f"Unknown tool: {name}"}

                return [TextContent(type="text", text=json.dumps(result, indent=2))]

            except Exception as e:
                error_result = {"error": f"Tool execution failed: {str(e)}"}
                return [TextContent(type="text", text=json.dumps(error_result, indent=2))]

    def _register_resources(self):
        """Register MCP resources"""
        if not self.server:
            return

        @self.server.list_resources()
        async def handle_list_resources() -> List[Resource]:
            """List available resources"""
            return [
                Resource(
                    uri="trading://portfolio",
                    name="Portfolio Status",
                    description="Current portfolio status and positions",
                    mimeType="application/json"
                ),
                Resource(
                    uri="trading://market_data",
                    name="Market Data",
                    description="Real-time market data and price feeds",
                    mimeType="application/json"
                ),
                Resource(
                    uri="trading://agents",
                    name="Trading Agents",
                    description="Information about active trading agents",
                    mimeType="application/json"
                ),
                Resource(
                    uri="trading://strategies",
                    name="Trading Strategies",
                    description="Available trading strategies and their performance",
                    mimeType="application/json"
                ),
                Resource(
                    uri="memory://agent_memories",
                    name="Agent Memories",
                    description="Persistent agent memories and learning data",
                    mimeType="application/json"
                )
            ]

        @self.server.read_resource()
        async def handle_read_resource(uri: str) -> str:
            """Handle resource reads"""
            try:
                if uri == "trading://portfolio":
                    result = await self._get_portfolio_resource()
                elif uri == "trading://market_data":
                    result = await self._get_market_data_resource()
                elif uri == "trading://agents":
                    result = await self._get_agents_resource()
                elif uri == "trading://strategies":
                    result = await self._get_strategies_resource()
                elif uri == "memory://agent_memories":
                    result = await self._get_memories_resource()
                else:
                    result = {"error": f"Unknown resource: {uri}"}

                return json.dumps(result, indent=2)

            except Exception as e:
                error_result = {"error": f"Resource read failed: {str(e)}"}
                return json.dumps(error_result, indent=2)

    # Tool handlers
    async def _handle_trading_analysis(self, arguments: Dict[str, Any]) -> Dict[str, Any]:
        """Handle trading analysis tool"""
        symbol = arguments.get("symbol", "BTC")
        timeframe = arguments.get("timeframe", "1h")
        strategy = arguments.get("strategy", "momentum")

        autogen = self.integrations.get("autogen")
        if autogen and autogen.is_available():
            # Get market data first
            market_data = {
                "symbol": symbol,
                "timeframe": timeframe,
                "strategy": strategy,
                "timestamp": datetime.now().isoformat()
            }

            # Run analysis with AutoGen
            analysis = await autogen.run_trading_analysis(market_data)
            return analysis
        else:
            return {
                "error": "AutoGen not available",
                "fallback_analysis": f"Basic analysis for {symbol} - AutoGen multi-agent system not initialized"
            }

    async def _handle_execute_trade(self, arguments: Dict[str, Any]) -> Dict[str, Any]:
        """Handle trade execution tool"""
        symbol = arguments.get("symbol")
        side = arguments.get("side")
        quantity = arguments.get("quantity")
        paper_trade = arguments.get("paper_trade", True)

        if not all([symbol, side, quantity]):
            return {"error": "Missing required parameters: symbol, side, quantity"}

        # Execute paper trade (real trading would require additional security)
        trade_data = {
            "symbol": symbol,
            "side": side,
            "quantity": quantity,
            "paper_trade": paper_trade,
            "timestamp": datetime.now().isoformat(),
            "status": "executed"
        }

        # Store in database
        supabase = self.integrations.get("supabase")
        if supabase and supabase.is_available():
            await supabase.log_system_event("trade_executed", trade_data)

        return trade_data

    async def _handle_portfolio_status(self, arguments: Dict[str, Any]) -> Dict[str, Any]:
        """Handle portfolio status tool"""
        include_history = arguments.get("include_history", False)

        # Mock portfolio data - in production this would fetch from trading system
        portfolio = {
            "total_value": 10000.0,
            "available_cash": 5000.0,
            "positions": [
                {"symbol": "BTC", "quantity": 0.1, "current_value": 3000.0},
                {"symbol": "ETH", "quantity": 1.0, "current_value": 2000.0}
            ],
            "pnl": {
                "realized": 150.0,
                "unrealized": 300.0
            },
            "timestamp": datetime.now().isoformat()
        }

        if include_history:
            portfolio["history"] = [
                {"date": "2024-01-01", "value": 9500.0},
                {"date": "2024-01-02", "value": 10200.0}
            ]

        return portfolio

    async def _handle_market_data(self, arguments: Dict[str, Any]) -> Dict[str, Any]:
        """Handle market data tool"""
        symbol = arguments.get("symbol", "BTC")
        timeframe = arguments.get("timeframe", "1h")
        limit = arguments.get("limit", 100)

        # Mock market data - in production this would fetch from data providers
        market_data = {
            "symbol": symbol,
            "timeframe": timeframe,
            "current_price": 50000.0 if symbol == "BTC" else 3000.0,
            "volume_24h": 1000000,
            "change_24h": 2.5,
            "data_points": limit,
            "timestamp": datetime.now().isoformat()
        }

        return market_data

    async def _handle_create_trading_agent(self, arguments: Dict[str, Any]) -> Dict[str, Any]:
        """Handle create trading agent tool"""
        name = arguments.get("name")
        strategy = arguments.get("strategy")
        risk_tolerance = arguments.get("risk_tolerance", "medium")

        if not all([name, strategy]):
            return {"error": "Missing required parameters: name, strategy"}

        autogen = self.integrations.get("autogen")
        if autogen and autogen.is_available():
            # Create custom agent team
            agents_config = [
                {
                    "name": f"{name}_Analyst",
                    "system_message": f"You are a {strategy} strategy analyst with {risk_tolerance} risk tolerance.",
                    "model_name": "gpt-4o-mini"
                },
                {
                    "name": f"{name}_Executor", 
                    "system_message": f"You execute {strategy} trades with {risk_tolerance} risk management.",
                    "model_name": "gpt-4o-mini"
                }
            ]

            success = autogen.create_custom_team(name, agents_config)

            if success:
                return {
                    "status": "created",
                    "agent_name": name,
                    "strategy": strategy,
                    "risk_tolerance": risk_tolerance,
                    "team_members": [config["name"] for config in agents_config]
                }
            else:
                return {"error": f"Failed to create agent team {name}"}
        else:
            return {"error": "AutoGen not available for agent creation"}

    async def _handle_memory_search(self, arguments: Dict[str, Any]) -> Dict[str, Any]:
        """Handle memory search tool"""
        query = arguments.get("query")
        agent_id = arguments.get("agent_id", "default")
        memory_type = arguments.get("memory_type")
        limit = arguments.get("limit", 10)

        if not query:
            return {"error": "Query parameter is required"}

        memory = self.integrations.get("memory")
        if memory and memory.is_available():
            # Create a mock agent for the search
            class MockAgent:
                def __init__(self, agent_id):
                    self.agent_name = agent_id
                    self.number = 0

            mock_agent = MockAgent(agent_id)
            memories = await memory.recall_memories(mock_agent, query, limit)

            return {
                "query": query,
                "agent_id": agent_id,
                "results": [
                    {
                        "content": mem.content,
                        "type": mem.memory_type,
                        "importance": mem.importance,
                        "timestamp": mem.timestamp.isoformat()
                    }
                    for mem in memories
                ],
                "total_found": len(memories)
            }
        else:
            return {"error": "Memory system not available"}

    async def _handle_store_decision(self, arguments: Dict[str, Any]) -> Dict[str, Any]:
        """Handle store trading decision tool"""
        decision = arguments.get("decision", {})
        reasoning = arguments.get("reasoning", "")
        confidence = arguments.get("confidence", 0.5)
        importance = arguments.get("importance", 0.7)

        # Store in memory system
        memory = self.integrations.get("memory")
        supabase = self.integrations.get("supabase")

        decision_data = {
            **decision,
            "reasoning": reasoning,
            "confidence": confidence,
            "timestamp": datetime.now().isoformat()
        }

        success = False

        if memory and memory.is_available():
            class MockAgent:
                def __init__(self):
                    self.agent_name = "trading_agent"
                    self.number = 0

            mock_agent = MockAgent()
            success = await memory.store_agent_memory(
                mock_agent,
                f"Trading Decision: {json.dumps(decision_data)}",
                memory_type="episodic",
                context=decision_data,
                importance=importance
            )

        if supabase and supabase.is_available():
            await supabase.log_system_event("trading_decision", decision_data)

        return {
            "status": "stored" if success else "failed",
            "decision": decision_data,
            "stored_in_memory": success,
            "stored_in_database": supabase is not None
        }

    async def _handle_run_strategy(self, arguments: Dict[str, Any]) -> Dict[str, Any]:
        """Handle run trading strategy tool"""
        strategy = arguments.get("strategy")
        symbol = arguments.get("symbol")
        agent_team = arguments.get("agent_team")
        parameters = arguments.get("parameters", {})

        if not all([strategy, symbol]):
            return {"error": "Missing required parameters: strategy, symbol"}

        autogen = self.integrations.get("autogen")
        if autogen and autogen.is_available():
            strategy_prompt = f"""
            Execute {strategy} trading strategy for {symbol}.
            Parameters: {json.dumps(parameters)}

            Analyze the market and provide:
            1. Entry signal (yes/no)
            2. Position size recommendation
            3. Risk management parameters
            4. Expected profit target
            """

            if agent_team:
                result = await autogen.run_custom_team(agent_team, strategy_prompt)
            else:
                market_data = {"symbol": symbol, "strategy": strategy, "parameters": parameters}
                result = await autogen.run_trading_analysis(market_data)

            return {
                "strategy": strategy,
                "symbol": symbol,
                "result": result,
                "timestamp": datetime.now().isoformat()
            }
        else:
            return {"error": "AutoGen not available for strategy execution"}

    # Resource handlers
    async def _get_portfolio_resource(self) -> Dict[str, Any]:
        """Get portfolio resource data"""
        return await self._handle_portfolio_status({"include_history": True})

    async def _get_market_data_resource(self) -> Dict[str, Any]:
        """Get market data resource"""
        return {
            "markets": ["BTC", "ETH", "ADA", "SOL"],
            "timeframes": ["1m", "5m", "15m", "1h", "4h", "1d"],
            "data_sources": ["hyperliquid", "trading_api", "mock"],
            "last_updated": datetime.now().isoformat()
        }

    async def _get_agents_resource(self) -> Dict[str, Any]:
        """Get trading agents resource"""
        return {
            "active_agents": ["MarketAnalyst", "RiskManager", "ExecutionAgent"],
            "available_strategies": ["momentum", "mean_reversion", "breakout", "scalping"],
            "total_teams": 1,
            "last_updated": datetime.now().isoformat()
        }

    async def _get_strategies_resource(self) -> Dict[str, Any]:
        """Get trading strategies resource"""
        return {
            "strategies": [
                {
                    "name": "momentum",
                    "description": "Trend following strategy",
                    "risk_level": "medium",
                    "performance": {"win_rate": 0.65, "profit_factor": 1.4}
                },
                {
                    "name": "mean_reversion", 
                    "description": "Counter-trend strategy",
                    "risk_level": "high",
                    "performance": {"win_rate": 0.58, "profit_factor": 1.2}
                }
            ],
            "total_strategies": 2,
            "last_updated": datetime.now().isoformat()
        }

    async def _get_memories_resource(self) -> Dict[str, Any]:
        """Get agent memories resource"""
        memory = self.integrations.get("memory")
        if memory and memory.is_available():
            return {
                "memory_system": "letta",
                "status": "available",
                "total_agents": len(getattr(memory.memory_system, 'agent_memories', {})),
                "last_updated": datetime.now().isoformat()
            }
        else:
            return {
                "memory_system": "none",
                "status": "unavailable",
                "error": "Memory system not initialized"
            }

    async def run(self):
        """Run the MCP server"""
        if not MCP_AVAILABLE:
            PrintStyle(font_color="red", padding=True).print("MCP not available - install with: pip install mcp")
            return

        if not self.server:
            PrintStyle(font_color="red", padding=True).print("MCP server not initialized")
            return

        PrintStyle(font_color="green", padding=True).print(f"Starting Enhanced MCP Server: {self.name}")

        try:
            # Initialize integrations if not already done
            if not self.integrations:
                self._initialize_integrations()

            # Run the server
            async with mcp.server.stdio.stdio_server() as (read_stream, write_stream):
                await self.server.run(
                    read_stream,
                    write_stream,
                    InitializationOptions(
                        server_name=self.name,
                        server_version="1.0.0",
                        capabilities=self.server.get_capabilities()
                    )
                )
        except Exception as e:
            PrintStyle(font_color="red", padding=True).print(f"MCP server error: {e}")


# Global server instance
enhanced_mcp_server = None

def get_enhanced_mcp_server():
    """Get or create the global MCP server instance"""
    global enhanced_mcp_server
    if enhanced_mcp_server is None:
        enhanced_mcp_server = EnhancedMCPServer()
    return enhanced_mcp_server

def run_mcp_server():
    """Run the MCP server (for CLI usage)"""
    server = get_enhanced_mcp_server()
    asyncio.run(server.run())

