"""
Hyperliquid Trading Integration for Agent Zero
Provides live trading capabilities through Hyperliquid DEX
"""

import asyncio
import json
from typing import Dict, List, Optional, Any, Union
from datetime import datetime
import os

from python.helpers.tool import Tool, Response
from python.helpers.print_style import PrintStyle
import python.helpers.log as Log

try:
    from hyperliquid.info import Info
    from hyperliquid.exchange import Exchange
    from hyperliquid.utils import constants
    import eth_account
    HYPERLIQUID_AVAILABLE = True
except ImportError:
    HYPERLIQUID_AVAILABLE = False

# Import our enhanced integrations
from python.helpers.redis_integration import get_redis_manager
from python.helpers.supabase_integration import get_supabase_manager


class HyperliquidTrading(Tool):
    """Hyperliquid trading integration tool for Agent Zero"""

    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.info = None
        self.exchange = None
        self.redis = None
        self.supabase = None
        self._initialize_connections()

    def _initialize_connections(self):
        """Initialize Hyperliquid and other connections"""
        try:
            if HYPERLIQUID_AVAILABLE:
                # Initialize Hyperliquid Info (public data)
                self.info = Info()

                # Initialize exchange only if private key is available
                private_key = os.getenv("HYPERLIQUID_PRIVATE_KEY")
                if private_key:
                    account = eth_account.Account.from_key(private_key)
                    self.exchange = Exchange(account, constants.MAINNET_API_URL)
                    PrintStyle(font_color="green", padding=True).print("Hyperliquid exchange connection established")
                else:
                    PrintStyle(font_color="yellow", padding=True).print("Hyperliquid private key not found - read-only mode")

            # Initialize other integrations
            self.redis = get_redis_manager(self.agent.context)
            self.supabase = get_supabase_manager(self.agent.context)

            # Initialize async components
            asyncio.create_task(self._async_initialize())

        except Exception as e:
            PrintStyle(font_color="red", padding=True).print(f"Hyperliquid initialization error: {e}")

    async def _async_initialize(self):
        """Initialize async components"""
        try:
            if self.redis:
                await self.redis.initialize()

            if self.supabase:
                self.supabase.initialize()

        except Exception as e:
            PrintStyle(font_color="red", padding=True).print(f"Async initialization error: {e}")

    async def execute(self, **kwargs):
        """Main execution method for Hyperliquid operations"""
        action = kwargs.get("action", "")

        if not action:
            return Response(
                message="Hyperliquid tool requires an action parameter. Available actions: info, trade, portfolio, orders, cancel, market_data",
                break_loop=False
            )

        if not HYPERLIQUID_AVAILABLE:
            return Response(
                message="Hyperliquid not available. Install with: pip install hyperliquid",
                break_loop=False
            )

        try:
            if action == "info":
                result = await self._get_exchange_info(kwargs)
            elif action == "trade":
                result = await self._execute_trade(kwargs)
            elif action == "portfolio":
                result = await self._get_portfolio(kwargs)
            elif action == "orders":
                result = await self._get_orders(kwargs)
            elif action == "cancel":
                result = await self._cancel_order(kwargs)
            elif action == "market_data":
                result = await self._get_market_data(kwargs)
            elif action == "funding_rates":
                result = await self._get_funding_rates(kwargs)
            elif action == "order_book":
                result = await self._get_order_book(kwargs)
            else:
                result = {
                    "error": f"Unknown action: {action}",
                    "available_actions": ["info", "trade", "portfolio", "orders", "cancel", "market_data", "funding_rates", "order_book"]
                }

            # Store result in cache
            if not result.get("error") and self.redis and self.redis.is_available():
                cache_key = f"hyperliquid:{action}:{datetime.now().strftime('%Y%m%d_%H%M')}"
                await self.redis.cache_set(cache_key, result, 300)

            return Response(message=json.dumps(result, indent=2), break_loop=False)

        except Exception as e:
            error_msg = f"Hyperliquid operation error: {str(e)}"
            PrintStyle(font_color="red", padding=True).print(error_msg)
            return Response(message=error_msg, break_loop=False)

    async def _get_exchange_info(self, kwargs) -> Dict[str, Any]:
        """Get exchange information"""
        try:
            if not self.info:
                return {"error": "Hyperliquid info not available"}

            # Get exchange metadata
            meta = self.info.meta()

            # Get all current prices
            all_mids = self.info.all_mids()

            # Get universe of tradeable assets
            universe = meta.get('universe', [])

            result = {
                "exchange": "hyperliquid",
                "total_assets": len(universe),
                "assets": universe[:20],  # First 20 assets
                "price_sample": dict(list(all_mids.items())[:10]),  # First 10 prices
                "timestamp": datetime.now().isoformat(),
                "status": "active"
            }

            return result

        except Exception as e:
            return {"error": f"Failed to get exchange info: {str(e)}"}

    async def _execute_trade(self, kwargs) -> Dict[str, Any]:
        """Execute a trade on Hyperliquid"""
        if not self.exchange:
            return {"error": "Hyperliquid exchange not available - private key required"}

        try:
            symbol = kwargs.get("symbol")
            is_buy = kwargs.get("is_buy", True)
            sz = kwargs.get("size", 0)
            limit_px = kwargs.get("limit_price")
            order_type = kwargs.get("order_type", "Limit")
            reduce_only = kwargs.get("reduce_only", False)

            if not symbol or sz <= 0:
                return {"error": "Symbol and size > 0 are required"}

            # Prepare order
            order = {
                "a": symbol,  # asset
                "b": is_buy,  # is_buy
                "p": str(limit_px) if limit_px else None,  # limit price
                "s": str(sz),  # size
                "r": reduce_only,  # reduce_only
                "t": {"limit": {"tif": "Gtc"}} if order_type == "Limit" else {"market": {}}
            }

            # Execute trade
            result = self.exchange.order(order)

            # Store trade record
            trade_record = {
                "symbol": symbol,
                "side": "buy" if is_buy else "sell",
                "size": sz,
                "limit_price": limit_px,
                "order_type": order_type,
                "reduce_only": reduce_only,
                "result": result,
                "timestamp": datetime.now().isoformat(),
                "exchange": "hyperliquid"
            }

            # Store in database
            if self.supabase and self.supabase.is_available():
                await self.supabase.store_trading_decision(self.agent, trade_record)

            return {
                "status": "executed",
                "trade": trade_record,
                "hyperliquid_response": result
            }

        except Exception as e:
            return {"error": f"Trade execution failed: {str(e)}"}

    async def _get_portfolio(self, kwargs) -> Dict[str, Any]:
        """Get portfolio information"""
        if not self.exchange:
            return {"error": "Hyperliquid exchange not available - private key required"}

        try:
            # Get user state
            user_state = self.info.user_state(self.exchange.account.address)

            # Extract portfolio information
            balances = user_state.get("balances", [])
            positions = user_state.get("assetPositions", [])

            portfolio = {
                "account": self.exchange.account.address,
                "balances": balances,
                "positions": positions,
                "total_positions": len(positions),
                "total_balance_entries": len(balances),
                "timestamp": datetime.now().isoformat()
            }

            # Calculate total portfolio value
            total_value = 0
            for balance in balances:
                if balance.get("coin") == "USDC":
                    total_value += float(balance.get("total", 0))

            portfolio["total_value_usdc"] = total_value

            return portfolio

        except Exception as e:
            return {"error": f"Failed to get portfolio: {str(e)}"}

    async def _get_orders(self, kwargs) -> Dict[str, Any]:
        """Get open orders"""
        if not self.exchange:
            return {"error": "Hyperliquid exchange not available - private key required"}

        try:
            # Get open orders
            open_orders = self.info.open_orders(self.exchange.account.address)

            # Get order history if requested
            include_history = kwargs.get("include_history", False)
            order_history = []

            if include_history:
                # Get recent fills
                fills = self.info.user_fills(self.exchange.account.address)
                order_history = fills[:50]  # Last 50 fills

            return {
                "open_orders": open_orders,
                "total_open": len(open_orders),
                "order_history": order_history,
                "total_history": len(order_history),
                "timestamp": datetime.now().isoformat()
            }

        except Exception as e:
            return {"error": f"Failed to get orders: {str(e)}"}

    async def _cancel_order(self, kwargs) -> Dict[str, Any]:
        """Cancel an order"""
        if not self.exchange:
            return {"error": "Hyperliquid exchange not available - private key required"}

        try:
            order_id = kwargs.get("order_id")
            symbol = kwargs.get("symbol")

            if not order_id:
                return {"error": "Order ID is required"}

            # Cancel order
            result = self.exchange.cancel(order_id)

            cancel_record = {
                "order_id": order_id,
                "symbol": symbol,
                "result": result,
                "timestamp": datetime.now().isoformat()
            }

            return {
                "status": "cancelled",
                "cancel_record": cancel_record,
                "hyperliquid_response": result
            }

        except Exception as e:
            return {"error": f"Order cancellation failed: {str(e)}"}

    async def _get_market_data(self, kwargs) -> Dict[str, Any]:
        """Get market data"""
        try:
            if not self.info:
                return {"error": "Hyperliquid info not available"}

            symbol = kwargs.get("symbol")
            timeframe = kwargs.get("timeframe", "1h")

            if symbol:
                # Get specific symbol data
                all_mids = self.info.all_mids()
                price = all_mids.get(symbol)

                if price is None:
                    return {"error": f"Symbol {symbol} not found"}

                # Get 24h data if available
                try:
                    stats_24h = self.info.meta_and_asset_ctxs()
                    asset_ctx = None
                    for ctx in stats_24h[1]:  # asset contexts
                        if ctx.get("coin") == symbol:
                            asset_ctx = ctx
                            break

                    result = {
                        "symbol": symbol,
                        "price": float(price),
                        "timeframe": timeframe,
                        "asset_context": asset_ctx,
                        "timestamp": datetime.now().isoformat(),
                        "source": "hyperliquid"
                    }

                except Exception:
                    result = {
                        "symbol": symbol,
                        "price": float(price),
                        "timeframe": timeframe,
                        "timestamp": datetime.now().isoformat(),
                        "source": "hyperliquid"
                    }
            else:
                # Get all market data
                all_mids = self.info.all_mids()
                meta = self.info.meta()

                result = {
                    "all_prices": all_mids,
                    "total_markets": len(all_mids),
                    "universe": meta.get("universe", []),
                    "timestamp": datetime.now().isoformat(),
                    "source": "hyperliquid"
                }

            return result

        except Exception as e:
            return {"error": f"Failed to get market data: {str(e)}"}

    async def _get_funding_rates(self, kwargs) -> Dict[str, Any]:
        """Get funding rates"""
        try:
            if not self.info:
                return {"error": "Hyperliquid info not available"}

            # Get meta and asset contexts for funding rates
            meta, asset_ctxs = self.info.meta_and_asset_ctxs()

            funding_rates = []
            for asset_ctx in asset_ctxs:
                if "funding" in asset_ctx:
                    funding_rates.append({
                        "symbol": asset_ctx.get("coin"),
                        "funding_rate": asset_ctx.get("funding"),
                        "predicted_funding": asset_ctx.get("nextFunding")
                    })

            return {
                "funding_rates": funding_rates,
                "total_assets": len(funding_rates),
                "timestamp": datetime.now().isoformat()
            }

        except Exception as e:
            return {"error": f"Failed to get funding rates: {str(e)}"}

    async def _get_order_book(self, kwargs) -> Dict[str, Any]:
        """Get order book data"""
        try:
            if not self.info:
                return {"error": "Hyperliquid info not available"}

            symbol = kwargs.get("symbol", "BTC")

            # Get order book
            l2_book = self.info.l2_snapshot(symbol)

            if not l2_book:
                return {"error": f"No order book data for {symbol}"}

            return {
                "symbol": symbol,
                "order_book": l2_book,
                "timestamp": datetime.now().isoformat(),
                "source": "hyperliquid"
            }

        except Exception as e:
            return {"error": f"Failed to get order book: {str(e)}"}

    def is_available(self) -> bool:
        """Check if Hyperliquid is available"""
        return HYPERLIQUID_AVAILABLE and self.info is not None

    def has_trading_access(self) -> bool:
        """Check if trading access is available"""
        return self.is_available() and self.exchange is not None

