/* Trading System UI Styles for Agent Zero */

/* Trading Dashboard Styles */
.trading-dashboard {
    padding: 20px;
    background: var(--bg-color);
    border-radius: 8px;
    margin-bottom: 20px;
}

.trading-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 20px;
    padding-bottom: 10px;
    border-bottom: 1px solid var(--border-color);
}

.trading-title {
    font-size: 1.5em;
    font-weight: bold;
    color: var(--color-primary);
}

.trading-status {
    display: flex;
    align-items: center;
    gap: 10px;
}

.status-indicator {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: #28a745;
    box-shadow: 0 0 8px rgba(40, 167, 69, 0.5);
    animation: pulse 2s infinite;
}

.status-indicator.disconnected {
    background: #dc3545;
    box-shadow: 0 0 8px rgba(220, 53, 69, 0.5);
}

@keyframes pulse {
    0% { opacity: 1; }
    50% { opacity: 0.5; }
    100% { opacity: 1; }
}

/* Trading Cards */
.trading-cards {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 20px;
    margin-bottom: 20px;
}

.trading-card {
    background: var(--message-bg-color);
    border-radius: 8px;
    padding: 20px;
    border: 1px solid var(--border-color);
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease;
}

.trading-card:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 16px rgba(0, 0, 0, 0.15);
}

.card-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 15px;
}

.card-title {
    font-size: 1.2em;
    font-weight: 600;
    color: var(--color-primary);
}

.card-icon {
    width: 24px;
    height: 24px;
    opacity: 0.7;
}

.card-content {
    space-y: 10px;
}

.metric-row {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 8px 0;
    border-bottom: 1px solid var(--border-color-light);
}

.metric-row:last-child {
    border-bottom: none;
}

.metric-label {
    color: var(--text-color-muted);
    font-size: 0.9em;
}

.metric-value {
    font-weight: 600;
    color: var(--text-color);
}

.metric-value.positive {
    color: #28a745;
}

.metric-value.negative {
    color: #dc3545;
}

/* Trading Controls */
.trading-controls {
    background: var(--message-bg-color);
    border-radius: 8px;
    padding: 20px;
    border: 1px solid var(--border-color);
    margin-bottom: 20px;
}

.control-row {
    display: flex;
    gap: 15px;
    margin-bottom: 15px;
    align-items: center;
    flex-wrap: wrap;
}

