input.content:
/**
 * Trading System JavaScript for Agent Zero
 * Provides enhanced trading capabilities and multi-agent management
 */

// Trading system state
let tradingSystem = {
    connected: false,
    autogen_available: false,
    memory_available: false,
    redis_available: false,
    supabase_available: false,
    hyperliquid_available: false,
    agents: new Map(),
    portfolio: {},
    market_data: {},
    trading_history: [],
    active_strategies: [],
    ws_connection: null
};

// Initialize trading system
function initializeTradingSystem() {
    console.log('Initializing Trading System...');

    // Check system capabilities
    checkSystemCapabilities();

    // Setup WebSocket connection for real-time updates
    setupWebSocket();

    // Initialize dashboard
    initializeTradingDashboard();

    // Start periodic updates
    startPeriodicUpdates();
}

// Check what trading capabilities are available
async function checkSystemCapabilities() {
    try {
        const response = await fetch('/api/trading_system', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ action: 'capabilities' })
        });

        const result = await response.json();
        if (result.capabilities) {
            tradingSystem.autogen_available = result.capabilities.autogen || false;
            tradingSystem.memory_available = result.capabilities.memory || false;
            tradingSystem.redis_available = result.capabilities.redis || false;
            tradingSystem.supabase_available = result.capabilities.supabase || false;
            tradingSystem.hyperliquid_available = result.capabilities.hyperliquid || false;
        }

        updateSystemStatus();

    } catch (error) {
        console.error('Failed to check system capabilities:', error);
    }
}

// Update system status indicators
function updateSystemStatus() {
    const statusContainer = document.getElementById('trading-system-status');
    if (!statusContainer) return;

    const capabilities = [
        { name: 'AutoGen', available: tradingSystem.autogen_available },
        { name: 'Memory', available: tradingSystem.memory_available },
        { name: 'Redis', available: tradingSystem.redis_available },
        { name: 'Supabase', available: tradingSystem.supabase_available },
        { name: 'Hyperliquid', available: tradingSystem.hyperliquid_available }
    ];

    statusContainer.innerHTML = capabilities.map(cap => `
        <div class="capability-status">
            <span class="status-indicator ${cap.available ? 'connected' : 'disconnected'}"></span>
            <span class="capability-name">${cap.name}</span>
        </div>
    `).join('');
}

// Setup WebSocket for real-time updates
function setupWebSocket() {
    // This would connect to a WebSocket endpoint for real-time trading updates
    // For now, we'll simulate with periodic polling
    console.log('WebSocket setup would go here for real-time updates');
}

// Initialize the trading dashboard
function initializeTradingDashboard() {
    const dashboardContainer = document.getElementById('trading-dashboard');
    if (!dashboardContainer) return;

    dashboardContainer.innerHTML = `
        <div class="trading-header">
            <h2 class="trading-title">Trading System</h2>
            <div class="trading-status">
                <span class="status-indicator ${tradingSystem.connected ? 'connected' : 'disconnected'}"></span>
                <span>System Status</span>
            </div>
        </div>

        <div class="trading-tabs">
            <button class="trading-tab active" onclick="showTradingTab('dashboard')">Dashboard</button>
            <button class="trading-tab" onclick="showTradingTab('agents')">Agents</button>
            <button class="trading-tab" onclick="showTradingTab('trading')">Trading</button>
            <button class="trading-tab" onclick="showTradingTab('analysis')">Analysis</button>
            <button class="trading-tab" onclick="showTradingTab('history')">History</button>
        </div>

        <div id="trading-tab-dashboard" class="trading-tab-content active">
            ${createDashboardTab()}
        </div>

        <div id="trading-tab-agents" class="trading-tab-content">
            ${createAgentsTab()}
        </div>

        <div id="trading-tab-trading" class="trading-tab-content">
            ${createTradingTab()}
        </div>

        <div id="trading-tab-analysis" class="trading-tab-content">
            ${createAnalysisTab()}
        </div>

        <div id="trading-tab-history" class="trading-tab-content">
            ${createHistoryTab()}
        </div>
    `;
}

// Create dashboard tab content
function createDashboardTab() {
    return `
        <div class="trading-cards">
            <div class="trading-card">
                <div class="card-header">
                    <h3 class="card-title">Portfolio</h3>
                    <svg class="card-icon" viewBox="0 0 24 24" fill="currentColor">
                        <path d="M12 2L2 7v10c0 5.55 3.84 9.95 9 11 5.16-1.05 9-5.45 9-11V7l-10-5z"/>
                    </svg>
                </div>
                <div class="card-content">
                    <div class="metric-row">
                        <span class="metric-label">Total Value</span>
                        <span class="metric-value" id="portfolio-total">$0.00</span>
                    </div>
                    <div class="metric-row">
                        <span class="metric-label">Available Cash</span>
                        <span class="metric-value" id="portfolio-cash">$0.00</span>
                    </div>
                    <div class="metric-row">
                        <span class="metric-label">P&L (24h)</span>
                        <span class="metric-value" id="portfolio-pnl">$0.00</span>
                    </div>
                </div>
            </div>

            <div class="trading-card">
                <div class="card-header">
                    <h3 class="card-title">Active Agents</h3>
                    <svg class="card-icon" viewBox="0 0 24 24" fill="currentColor">
                        <path d="M12 2C13.1 2 14 2.9 14 4C14 5.1 13.1 6 12 6C10.9 6 10 5.1 10 4C10 2.9 10.9 2 12 2ZM21 9V7L15 4V2.5C15 1.7 14.3 1 13.5 1S12 1.7 12 2.5V4L6 7V9C6 10.1 6.9 11 8 11H11V19C11 20.1 11.9 21 13 21S15 20.1 15 19V11H16C17.1 11 18 10.1 18 9Z"/>
                    </svg>
                </div>
                <div class="card-content">
                    <div class="metric-row">
                        <span class="metric-label">Trading Agents</span>
                        <span class="metric-value" id="active-agents">0</span>
                    </div>
                    <div class="metric-row">
                        <span class="metric-label">AutoGen Teams</span>
                        <span class="metric-value" id="autogen-teams">0</span>
                    </div>
                    <div class="metric-row">
                        <span class="metric-label">Active Strategies</span>
                        <span class="metric-value" id="active-strategies">0</span>
                    </div>
                </div>
            </div>

            <div class="trading-card">
                <div class="card-header">
                    <h3 class="card-title">System Health</h3>
                    <svg class="card-icon" viewBox="0 0 24 24" fill="currentColor">
