                        <option value="paper">Paper Trade</option>
                        <option value="live">Live Trade</option>
                    </select>
                </div>
                <button class="trading-button success" onclick="executeTrade()">Execute Trade</button>
                <button class="trading-button secondary" onclick="getMarketData()">Get Price</button>
            </div>
        </div>

        <div class="trading-controls">
            <h3>Portfolio Management</h3>
            <div class="control-row">
                <button class="trading-button primary" onclick="getPortfolioStatus()">Refresh Portfolio</button>
                <button class="trading-button secondary" onclick="getOpenOrders()">View Orders</button>
                <button class="trading-button secondary" onclick="getTradingHistory()">View History</button>
