"""
Enhanced Trading System API for Agent Zero
Provides API endpoints for trading functionality and system management
"""

import asyncio
import json
from typing import Dict, Any, Optional
from datetime import datetime

from python.helpers.api import ApiHandler
from python.helpers.print_style import PrintStyle

# Import trading tools
from python.tools.trading_system import TradingSystem
from python.tools.hyperliquid_trading import HyperliquidTrading

# Import enhanced integrations
from python.helpers.autogen_integration import get_autogen_integration
from python.helpers.letta_memory import get_memory_integration
from python.helpers.redis_integration import get_redis_manager
from python.helpers.supabase_integration import get_supabase_manager


class TradingSystemAPI(ApiHandler):
    """API handler for the enhanced trading system"""

    def __init__(self, app, lock):
        super().__init__(app, lock)
        self.trading_tool = None
        self.hyperliquid_tool = None
        self.autogen = None
        self.memory = None
        self.redis = None
        self.supabase = None
        self._initialize_components()

    def _initialize_components(self):
        """Initialize trading components"""
        try:
            # Create mock agent for tool initialization
            class MockAgent:
                def __init__(self):
                    self.number = 0
                    self.agent_name = "trading_api_agent"
                    self.context = None

            mock_agent = MockAgent()

            # Initialize trading tools
            self.trading_tool = TradingSystem(agent=mock_agent)
            self.hyperliquid_tool = HyperliquidTrading(agent=mock_agent)

            # Initialize integrations
            self.autogen = get_autogen_integration()
            self.memory = get_memory_integration()
            self.redis = get_redis_manager()
            self.supabase = get_supabase_manager()

            # Initialize async components
            asyncio.create_task(self._async_initialize())

        except Exception as e:
            PrintStyle(font_color="red", padding=True).print(f"Trading API initialization error: {e}")

    async def _async_initialize(self):
        """Initialize async components"""
        try:
            if self.redis:
                await self.redis.initialize()

            if self.supabase:
                self.supabase.initialize()

        except Exception as e:
            PrintStyle(font_color="yellow", padding=True).print(f"Async initialization warning: {e}")

    @staticmethod
    def requires_auth():
        return True

    async def handle_request(self, request):
        """Handle trading system API requests"""
        try:
            if request.method == 'GET':
                return await self._handle_get_request(request)
            elif request.method == 'POST':
                return await self._handle_post_request(request)
            else:
                return self._error_response("Method not allowed", 405)

        except Exception as e:
            PrintStyle(font_color="red", padding=True).print(f"Trading API error: {e}")
            return self._error_response(f"Internal server error: {str(e)}", 500)

    async def _handle_get_request(self, request):
        """Handle GET requests"""
        # GET requests can be used for status checks, capabilities, etc.
        return self._json_response({
            "status": "operational",
            "capabilities": self._get_capabilities(),
            "timestamp": datetime.now().isoformat()
        })

    async def _handle_post_request(self, request):
        """Handle POST requests"""
        try:
            # Parse request data
            if hasattr(request, 'get_json'):
                data = request.get_json() or {}
            else:
                import json
                body = await request.body() if hasattr(request, 'body') else request.data
                data = json.loads(body) if body else {}

            action = data.get('action', '')

            if not action:
                return self._error_response("Action parameter required")

            # Route to appropriate handler
            if action == 'capabilities':
                return await self._handle_capabilities(data)
            elif action == 'analyze':
                return await self._handle_analyze(data)
            elif action == 'trade':
                return await self._handle_trade(data)
            elif action == 'portfolio':
                return await self._handle_portfolio(data)
            elif action == 'market_data':
                return await self._handle_market_data(data)
            elif action == 'create_agent':
                return await self._handle_create_agent(data)
            elif action == 'run_strategy':
                return await self._handle_run_strategy(data)
            elif action == 'memory_recall':
                return await self._handle_memory_recall(data)
            elif action == 'store_decision':
                return await self._handle_store_decision(data)
            elif action == 'hyperliquid':
                return await self._handle_hyperliquid(data)
            else:
                return self._error_response(f"Unknown action: {action}")

        except Exception as e:
            return self._error_response(f"Request processing error: {str(e)}")

    async def _handle_capabilities(self, data):
        """Handle capabilities request"""
        return self._json_response({
            "capabilities": self._get_capabilities(),
            "status": "ok"
        })

    def _get_capabilities(self):
        """Get system capabilities"""
        return {
            "autogen": self.autogen and self.autogen.is_available() if self.autogen else False,
            "memory": self.memory and self.memory.is_available() if self.memory else False,
            "redis": self.redis and self.redis.is_available() if self.redis else False,
            "supabase": self.supabase and self.supabase.is_available() if self.supabase else False,
            "hyperliquid": self.hyperliquid_tool and self.hyperliquid_tool.is_available() if self.hyperliquid_tool else False,
            "trading_tools": self.trading_tool is not None
        }

    async def _handle_analyze(self, data):
        """Handle market analysis request"""
        try:
            if not self.trading_tool:
                return self._error_response("Trading tool not available")

            # Execute trading analysis
            result = await self.trading_tool.execute(**data)

            # Parse result message as JSON
            try:
                analysis_result = json.loads(result.message)
                return self._json_response(analysis_result)
            except json.JSONDecodeError:
                return self._json_response({"analysis": result.message})

        except Exception as e:
            return self._error_response(f"Analysis failed: {str(e)}")

    async def _handle_trade(self, data):
        """Handle trade execution request"""
        try:
            if not self.trading_tool:
                return self._error_response("Trading tool not available")

            # Execute trade
            result = await self.trading_tool.execute(**data)

            # Parse result message as JSON
            try:
                trade_result = json.loads(result.message)
                return self._json_response(trade_result)
            except json.JSONDecodeError:
                return self._json_response({"trade_result": result.message})

        except Exception as e:
            return self._error_response(f"Trade execution failed: {str(e)}")

    async def _handle_portfolio(self, data):
        """Handle portfolio status request"""
        try:
            if not self.trading_tool:
                return self._error_response("Trading tool not available")

            # Get portfolio status
            result = await self.trading_tool.execute(action="portfolio", **data)

            # Parse result message as JSON
            try:
                portfolio_result = json.loads(result.message)
                return self._json_response(portfolio_result)
            except json.JSONDecodeError:
                return self._json_response({"portfolio": result.message})

        except Exception as e:
            return self._error_response(f"Portfolio request failed: {str(e)}")

    async def _handle_market_data(self, data):
        """Handle market data request"""
        try:
            if not self.trading_tool:
                return self._error_response("Trading tool not available")

            # Get market data
            result = await self.trading_tool.execute(action="market_data", **data)

            # Parse result message as JSON
            try:
                market_result = json.loads(result.message)
                return self._json_response(market_result)
            except json.JSONDecodeError:
                return self._json_response({"market_data": result.message})

        except Exception as e:
            return self._error_response(f"Market data request failed: {str(e)}")

    async def _handle_create_agent(self, data):
        """Handle agent creation request"""
        try:
            if not self.trading_tool:
                return self._error_response("Trading tool not available")

            # Create trading agent
            result = await self.trading_tool.execute(action="create_agent", **data)

            # Parse result message as JSON
            try:
                agent_result = json.loads(result.message)
                return self._json_response(agent_result)
            except json.JSONDecodeError:
                return self._json_response({"agent_creation": result.message})

        except Exception as e:
            return self._error_response(f"Agent creation failed: {str(e)}")

    async def _handle_run_strategy(self, data):
        """Handle strategy execution request"""
        try:
            if not self.trading_tool:
                return self._error_response("Trading tool not available")

            # Run trading strategy
            result = await self.trading_tool.execute(action="run_strategy", **data)

            # Parse result message as JSON
            try:
                strategy_result = json.loads(result.message)
                return self._json_response(strategy_result)
            except json.JSONDecodeError:
                return self._json_response({"strategy_result": result.message})

        except Exception as e:
            return self._error_response(f"Strategy execution failed: {str(e)}")

    async def _handle_memory_recall(self, data):
        """Handle memory recall request"""
        try:
            if not self.trading_tool:
                return self._error_response("Trading tool not available")

            # Recall memories
            result = await self.trading_tool.execute(action="memory_recall", **data)

            # Parse result message as JSON
            try:
                memory_result = json.loads(result.message)
                return self._json_response(memory_result)
            except json.JSONDecodeError:
                return self._json_response({"memory_result": result.message})

        except Exception as e:
            return self._error_response(f"Memory recall failed: {str(e)}")

    async def _handle_store_decision(self, data):
        """Handle decision storage request"""
        try:
            if not self.trading_tool:
                return self._error_response("Trading tool not available")

            # Store trading decision
            result = await self.trading_tool.execute(action="store_decision", **data)

            # Parse result message as JSON
            try:
                storage_result = json.loads(result.message)
                return self._json_response(storage_result)
            except json.JSONDecodeError:
                return self._json_response({"storage_result": result.message})

        except Exception as e:
            return self._error_response(f"Decision storage failed: {str(e)}")

    async def _handle_hyperliquid(self, data):
        """Handle Hyperliquid-specific requests"""
        try:
            if not self.hyperliquid_tool:
                return self._error_response("Hyperliquid tool not available")

            # Remove 'hyperliquid' from data and use the sub-action
            hyperliquid_data = data.copy()
            hyperliquid_data.pop('action', None)

            # Execute Hyperliquid operation
            result = await self.hyperliquid_tool.execute(**hyperliquid_data)

            # Parse result message as JSON
            try:
                hyperliquid_result = json.loads(result.message)
                return self._json_response(hyperliquid_result)
            except json.JSONDecodeError:
                return self._json_response({"hyperliquid_result": result.message})

        except Exception as e:
            return self._error_response(f"Hyperliquid operation failed: {str(e)}")

    def _json_response(self, data, status_code=200):
        """Create JSON response"""
        from flask import jsonify

        response = jsonify(data)
        response.status_code = status_code
        return response

    def _error_response(self, message, status_code=400):
        """Create error response"""
