                    </div>
                    <div class="metric-row">
                        <span class="metric-label">Active Strategies</span>
                        <span class="metric-value" id="active-strategies">0</span>
                    </div>
                </div>
            </div>

            <div class="trading-card">
                <div class="card-header">
                    <h3 class="card-title">System Health</h3>
                    <svg class="card-icon" viewBox="0 0 24 24" fill="currentColor">
                        <path d="M12,2A10,10 0 0,0 2,12A10,10 0 0,0 12,22A10,10 0 0,0 22,12A10,10 0 0,0 12,2Z"/>
                    </svg>
                </div>
                <div class="card-content" id="trading-system-status">
                    <!-- System status indicators will be populated here -->
                </div>
            </div>
        </div>

        <div class="quick-actions">
            <button class="quick-action" onclick="refreshPortfolio()">Refresh Portfolio</button>
            <button class="quick-action" onclick="createQuickAgent()">Quick Agent</button>
            <button class="quick-action" onclick="runMarketAnalysis()">Market Analysis</button>
            <button class="quick-action" onclick="viewTradingSignals()">Trading Signals</button>
        </div>
    `;
}

// Create agents tab content
function createAgentsTab() {
    return `
        <div class="agent-creation">
            <h3>Create New Trading Agent</h3>
            <div class="agent-form">
                <div class="form-group">
                    <label class="form-label">Agent Name</label>
                    <input type="text" class="form-input" id="agent-name" placeholder="e.g., BTC Momentum Trader">
                </div>
                <div class="form-group">
                    <label class="form-label">Trading Strategy</label>
                    <select class="form-input" id="agent-strategy">
                        <option value="momentum">Momentum</option>
                        <option value="mean_reversion">Mean Reversion</option>
                        <option value="breakout">Breakout</option>
                        <option value="scalping">Scalping</option>
                        <option value="custom">Custom</option>
                    </select>
                </div>
                <div class="form-group">
                    <label class="form-label">Risk Tolerance</label>
                    <select class="form-input" id="agent-risk">
                        <option value="low">Low</option>
                        <option value="medium">Medium</option>
                        <option value="high">High</option>
                    </select>
                </div>
                <div class="form-group">
                    <label class="form-label">Target Symbol</label>
                    <input type="text" class="form-input" id="agent-symbol" placeholder="BTC, ETH, etc.">
                </div>
            </div>
            <div class="form-group">
                <label class="form-label">Agent Description</label>
                <textarea class="form-textarea" id="agent-description" placeholder="Describe the agent's purpose and behavior..."></textarea>
            </div>
            <button class="trading-button primary" onclick="createTradingAgent()">Create Agent</button>
        </div>

        <div class="trading-controls">
            <h3>Active Agents</h3>
            <div id="agents-list">
                <div class="empty-list-message">
                    <p><i>No agents created yet. Create your first agent above.</i></p>
                </div>
            </div>
        </div>
    `;
}

// Create trading tab content
function createTradingTab() {
    return `
        <div class="trading-controls">
            <h3>Quick Trade</h3>
            <div class="control-row">
                <div class="control-group">
                    <label class="control-label">Symbol</label>
                    <input type="text" class="control-input" id="trade-symbol" value="BTC" placeholder="BTC">
                </div>
                <div class="control-group">
                    <label class="control-label">Side</label>
                    <select class="control-select" id="trade-side">
                        <option value="buy">Buy</option>
                        <option value="sell">Sell</option>
                    </select>
                </div>
                <div class="control-group">
                    <label class="control-label">Quantity</label>
                    <input type="number" class="control-input" id="trade-quantity" step="0.0001" placeholder="0.1">
                </div>
                <div class="control-group">
                    <label class="control-label">Order Type</label>
                    <select class="control-select" id="trade-type">
                        <option value="market">Market</option>
                        <option value="limit">Limit</option>
                    </select>
                </div>
            </div>
            <div class="control-row">
                <div class="control-group">
                    <label class="control-label">Price (Limit Orders)</label>
                    <input type="number" class="control-input" id="trade-price" step="0.01" placeholder="50000">
                </div>
                <div class="control-group">
                    <label class="control-label">Trade Type</label>
                    <select class="control-select" id="trade-mode">
                        <option value="paper">Paper Trade</option>
                        <option value="live">Live Trade</option>
                    </select>
                </div>
                <button class="trading-button success" onclick="executeTrade()">Execute Trade</button>
                <button class="trading-button secondary" onclick="getMarketData()">Get Price</button>
            </div>
        </div>

        <div class="trading-controls">
            <h3>Portfolio Management</h3>
            <div class="control-row">
                <button class="trading-button primary" onclick="getPortfolioStatus()">Refresh Portfolio</button>
                <button class="trading-button secondary" onclick="getOpenOrders()">View Orders</button>
                <button class="trading-button secondary" onclick="getTradingHistory()">View History</button>
            </div>
        </div>
    `;
}

// Create analysis tab content
function createAnalysisTab() {
    return `
        <div class="trading-controls">
            <h3>Market Analysis</h3>
            <div class="control-row">
                <div class="control-group">
                    <label class="control-label">Symbol</label>
                    <input type="text" class="control-input" id="analysis-symbol" value="BTC" placeholder="BTC">
                </div>
                <div class="control-group">
                    <label class="control-label">Timeframe</label>
                    <select class="control-select" id="analysis-timeframe">
                        <option value="1m">1 Minute</option>
                        <option value="5m">5 Minutes</option>
                        <option value="15m">15 Minutes</option>
                        <option value="1h" selected>1 Hour</option>
                        <option value="4h">4 Hours</option>
                        <option value="1d">1 Day</option>
                    </select>
                </div>
                <div class="control-group">
                    <label class="control-label">Strategy</label>
                    <select class="control-select" id="analysis-strategy">
                        <option value="momentum">Momentum</option>
                        <option value="mean_reversion">Mean Reversion</option>
                        <option value="breakout">Breakout</option>
                        <option value="comprehensive">Comprehensive</option>
                    </select>
                </div>
                <button class="trading-button primary" onclick="runTradingAnalysis()">Analyze</button>
            </div>
        </div>

        <div class="trading-controls">
            <h3>Analysis Results</h3>
            <div id="analysis-results">
                <div class="empty-list-message">
                    <p><i>Run an analysis to see results here.</i></p>
                </div>
            </div>
        </div>
    `;
}

// Create history tab content
function createHistoryTab() {
    return `
        <div class="trading-history">
            <h3>Trading History</h3>
            <div class="control-row">
                <button class="trading-button secondary" onclick="loadTradingHistory()">Refresh History</button>
                <button class="trading-button secondary" onclick="exportTradingHistory()">Export</button>
            </div>
            <table class="history-table">
                <thead>
                    <tr>
                        <th>Time</th>
                        <th>Symbol</th>
                        <th>Side</th>
                        <th>Quantity</th>
                        <th>Price</th>
                        <th>Status</th>
                        <th>P&L</th>
                    </tr>
                </thead>
                <tbody id="history-table-body">
                    <tr>
                        <td colspan="7" style="text-align: center; padding: 20px;">
                            <i>No trading history available. Execute some trades to see history.</i>
                        </td>
                    </tr>
                </tbody>
            </table>
        </div>
    `;
}

// Show specific trading tab
function showTradingTab(tabName) {
    // Hide all tab contents
    document.querySelectorAll('.trading-tab-content').forEach(content => {
        content.classList.remove('active');
    });

    // Remove active class from all tabs
    document.querySelectorAll('.trading-tab').forEach(tab => {
        tab.classList.remove('active');
    });

    // Show selected tab content
    const selectedContent = document.getElementById(`trading-tab-${tabName}`);
    if (selectedContent) {
        selectedContent.classList.add('active');
    }

    // Add active class to selected tab
    event.target.classList.add('active');
}

// Trading API Functions
async function createTradingAgent() {
    const name = document.getElementById('agent-name').value;
    const strategy = document.getElementById('agent-strategy').value;
    const risk = document.getElementById('agent-risk').value;
    const symbol = document.getElementById('agent-symbol').value;
    const description = document.getElementById('agent-description').value;

    if (!name || !strategy) {
        showToast('Please provide agent name and strategy', 'error');
        return;
    }

    try {
        const response = await fetch('/api/trading_system', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                action: 'create_agent',
                name: name,
                strategy: strategy,
                risk_tolerance: risk,
                symbol: symbol,
                description: description
            })
        });

        const result = await response.json();

        if (result.error) {
            showToast(`Error: ${result.error}`, 'error');
        } else {
            showToast(`Agent "${name}" created successfully!`, 'success');
            // Clear form
            document.getElementById('agent-name').value = '';
            document.getElementById('agent-description').value = '';
            // Refresh agents list
            loadAgentsList();
        }

    } catch (error) {
        showToast(`Failed to create agent: ${error.message}`, 'error');
    }
}

async function executeTrade() {
    const symbol = document.getElementById('trade-symbol').value;
    const side = document.getElementById('trade-side').value;
    const quantity = document.getElementById('trade-quantity').value;
    const orderType = document.getElementById('trade-type').value;
    const price = document.getElementById('trade-price').value;
    const paperTrade = document.getElementById('trade-mode').value === 'paper';

    if (!symbol || !quantity) {
        showToast('Please provide symbol and quantity', 'error');
        return;
    }

    try {
        const tradeData = {
            action: 'trade',
            symbol: symbol,
            side: side,
            quantity: parseFloat(quantity),
            order_type: orderType,
            paper_trade: paperTrade
        };

        if (orderType === 'limit' && price) {
            tradeData.limit_price = parseFloat(price);
        }

        const response = await fetch('/api/trading_system', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(tradeData)
        });

        const result = await response.json();

        if (result.error) {
            showToast(`Trade Error: ${result.error}`, 'error');
        } else {
            showToast(`Trade executed: ${side} ${quantity} ${symbol}`, 'success');
            // Refresh portfolio
            refreshPortfolio();
        }

    } catch (error) {
        showToast(`Trade failed: ${error.message}`, 'error');
    }
}

async function runTradingAnalysis() {
    const symbol = document.getElementById('analysis-symbol').value;
    const timeframe = document.getElementById('analysis-timeframe').value;
    const strategy = document.getElementById('analysis-strategy').value;

    if (!symbol) {
        showToast('Please provide a symbol for analysis', 'error');
        return;
    }

    try {
        // Show loading state
        const resultsContainer = document.getElementById('analysis-results');
        resultsContainer.innerHTML = '<div class="loading-overlay"><div class="loading-spinner"></div></div>';

        const response = await fetch('/api/trading_system', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                action: 'analyze',
                symbol: symbol,
                timeframe: timeframe,
                strategy: strategy
            })
        });

        const result = await response.json();

        if (result.error) {
            resultsContainer.innerHTML = `<div class="error-message">Error: ${result.error}</div>`;
        } else {
            displayAnalysisResults(result, resultsContainer);
        }

    } catch (error) {
        document.getElementById('analysis-results').innerHTML = 
            `<div class="error-message">Analysis failed: ${error.message}</div>`;
    }
}

function displayAnalysisResults(result, container) {
    const analysis = result.analysis || {};
    const decision = analysis.decision || {};

    container.innerHTML = `
        <div class="analysis-result">
            <div class="analysis-header">
                <h4>Analysis for ${result.symbol} (${result.timeframe})</h4>
                <span class="analysis-timestamp">${new Date(result.timestamp).toLocaleString()}</span>
            </div>
            <div class="analysis-content">
                <div class="decision-summary">
                    <div class="decision-action">
                        <span class="decision-label">Recommendation:</span>
                        <span class="decision-value ${decision.action}">${decision.action || 'hold'}</span>
                    </div>
                    <div class="decision-confidence">
                        <span class="decision-label">Confidence:</span>
                        <span class="decision-value">${((decision.confidence || 0) * 100).toFixed(1)}%</span>
                    </div>
                </div>
                <div class="decision-reasoning">
                    <h5>Reasoning:</h5>
                    <p>${decision.reason || 'No reasoning provided'}</p>
                </div>
                ${analysis.conversation ? `
                <div class="agent-conversation">
                    <h5>Agent Conversation:</h5>
                    <div class="conversation-messages">
                        ${analysis.conversation.map(msg => `
                            <div class="conversation-message">
                                <strong>${msg.sender}:</strong> ${msg.content}
                            </div>
                        `).join('')}
                    </div>
                </div>
                ` : ''}
            </div>
        </div>
    `;
}

// Utility functions
async function refreshPortfolio() {
    try {
        const response = await fetch('/api/trading_system', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ action: 'portfolio' })
        });

        const result = await response.json();

        if (!result.error) {
            updatePortfolioDisplay(result);
        }

    } catch (error) {
        console.error('Failed to refresh portfolio:', error);
    }
}

function updatePortfolioDisplay(portfolio) {
    const totalElement = document.getElementById('portfolio-total');
    const cashElement = document.getElementById('portfolio-cash');
    const pnlElement = document.getElementById('portfolio-pnl');

    if (totalElement) totalElement.textContent = `$${(portfolio.total_value || 0).toFixed(2)}`;
    if (cashElement) cashElement.textContent = `$${(portfolio.available_balance || 0).toFixed(2)}`;

    if (pnlElement) {
        const pnl = portfolio.pnl?.unrealized || 0;
        pnlElement.textContent = `$${pnl.toFixed(2)}`;
        pnlElement.className = `metric-value ${pnl >= 0 ? 'positive' : 'negative'}`;
    }
}

function startPeriodicUpdates() {
    // Update portfolio every 30 seconds
    setInterval(refreshPortfolio, 30000);

    // Update system status every 60 seconds
    setInterval(checkSystemCapabilities, 60000);
}

// Quick action functions
function createQuickAgent() {
    showTradingTab('agents');
    document.getElementById('agent-name').focus();
}

function runMarketAnalysis() {
    showTradingTab('analysis');
    document.getElementById('analysis-symbol').focus();
}

function viewTradingSignals() {
    // This would open a modal or navigate to signals view
    showToast('Trading signals feature coming soon!', 'info');
}

async function getMarketData() {
    const symbol = document.getElementById('trade-symbol').value;

    if (!symbol) {
        showToast('Please enter a symbol', 'error');
        return;
    }

    try {
        const response = await fetch('/api/trading_system', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                action: 'market_data',
                symbol: symbol
            })
        });

        const result = await response.json();

        if (result.error) {
            showToast(`Error: ${result.error}`, 'error');
        } else {
            const price = result.price || result.current_price || 0;
            showToast(`${symbol} current price: $${price.toFixed(2)}`, 'info');

            // Update the price field if it's a limit order
            const priceInput = document.getElementById('trade-price');
            if (priceInput) {
                priceInput.value = price.toFixed(2);
            }
        }

    } catch (error) {
        showToast(`Failed to get market data: ${error.message}`, 'error');
    }
}

// Initialize when DOM is loaded or when trading tab is activated
document.addEventListener('DOMContentLoaded', function() {
    // Check if we're on a page with trading dashboard
    if (document.getElementById('trading-dashboard')) {
        initializeTradingSystem();
    }
});

// Make sure trading system is available globally
if (!window.tradingSystem) {
    window.tradingSystem = {
        initialize: initializeTradingSystem,
        createAgent: createTradingAgent,
        executeTrade: executeTrade,
        runAnalysis: runTradingAnalysis,
        refreshPortfolio: refreshPortfolio,
        showTab: showTradingTab,
        getMarketData: getMarketData
    };
}

// Export functions for global access
window.tradingSystem = {
    initialize: initializeTradingSystem,
    createAgent: createTradingAgent,
    executeTrade: executeTrade,
    runAnalysis: runTradingAnalysis,
    refreshPortfolio: refreshPortfolio,
    showTab: showTradingTab,
    getMarketData: getMarketData
};

