/* Trading System UI Styles for Agent Zero */

/* Trading Dashboard Styles */
.trading-dashboard {
    padding: 20px;
    background: var(--bg-color);
    border-radius: 8px;
    margin-bottom: 20px;
}

.trading-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 20px;
    padding-bottom: 10px;
    border-bottom: 1px solid var(--border-color);
}

.trading-title {
    font-size: 1.5em;
    font-weight: bold;
    color: var(--color-primary);
}

.trading-status {
    display: flex;
    align-items: center;
    gap: 10px;
}

.status-indicator {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: #28a745;
    box-shadow: 0 0 8px rgba(40, 167, 69, 0.5);
    animation: pulse 2s infinite;
}

.status-indicator.disconnected {
    background: #dc3545;
    box-shadow: 0 0 8px rgba(220, 53, 69, 0.5);
}

@keyframes pulse {
    0% { opacity: 1; }
    50% { opacity: 0.5; }
    100% { opacity: 1; }
}

/* Trading Cards */
.trading-cards {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 20px;
    margin-bottom: 20px;
}

.trading-card {
    background: var(--message-bg-color);
    border-radius: 8px;
    padding: 20px;
    border: 1px solid var(--border-color);
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease;
}

.trading-card:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 16px rgba(0, 0, 0, 0.15);
}

.card-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 15px;
}

.card-title {
    font-size: 1.2em;
    font-weight: 600;
    color: var(--color-primary);
}

.card-icon {
    width: 24px;
    height: 24px;
    opacity: 0.7;
}

.card-content {
    space-y: 10px;
}

.metric-row {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 8px 0;
    border-bottom: 1px solid var(--border-color-light);
}

.metric-row:last-child {
    border-bottom: none;
}

.metric-label {
    color: var(--text-color-muted);
    font-size: 0.9em;
}

.metric-value {
    font-weight: 600;
    color: var(--text-color);
}

.metric-value.positive {
    color: #28a745;
}

.metric-value.negative {
    color: #dc3545;
}

/* Trading Controls */
.trading-controls {
    background: var(--message-bg-color);
    border-radius: 8px;
    padding: 20px;
    border: 1px solid var(--border-color);
    margin-bottom: 20px;
}

.control-row {
    display: flex;
    gap: 15px;
    margin-bottom: 15px;
    align-items: center;
    flex-wrap: wrap;
}

.control-group {
    display: flex;
    flex-direction: column;
    gap: 5px;
    min-width: 120px;
}

.control-label {
    font-size: 0.9em;
    color: var(--text-color-muted);
    font-weight: 500;
}

.control-input {
    padding: 8px 12px;
    border: 1px solid var(--border-color);
    border-radius: 4px;
    background: var(--bg-color);
    color: var(--text-color);
    font-size: 0.9em;
}

.control-input:focus {
    outline: none;
    border-color: var(--color-primary);
    box-shadow: 0 0 0 2px rgba(var(--color-primary-rgb), 0.2);
}

.control-select {
    padding: 8px 12px;
    border: 1px solid var(--border-color);
    border-radius: 4px;
    background: var(--bg-color);
    color: var(--text-color);
    font-size: 0.9em;
    cursor: pointer;
}

/* Trading Buttons */
.trading-button {
    padding: 10px 20px;
    border: none;
    border-radius: 6px;
    font-weight: 600;
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 0.9em;
    min-width: 100px;
}

.trading-button.primary {
    background: var(--color-primary);
    color: white;
}

.trading-button.primary:hover {
    background: var(--color-primary-dark);
    transform: translateY(-1px);
}

.trading-button.success {
    background: #28a745;
    color: white;
}

.trading-button.success:hover {
    background: #218838;
    transform: translateY(-1px);
}

.trading-button.danger {
    background: #dc3545;
    color: white;
}

.trading-button.danger:hover {
    background: #c82333;
    transform: translateY(-1px);
}

.trading-button.secondary {
    background: transparent;
    color: var(--text-color);
    border: 1px solid var(--border-color);
}

.trading-button.secondary:hover {
    background: var(--border-color);
}

.trading-button:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none !important;
}

/* Agent Creation Section */
.agent-creation {
    background: var(--message-bg-color);
    border-radius: 8px;
    padding: 20px;
    border: 1px solid var(--border-color);
    margin-bottom: 20px;
}

.agent-form {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 15px;
    margin-bottom: 20px;
}

.form-group {
    display: flex;
    flex-direction: column;
    gap: 5px;
}

.form-label {
    font-size: 0.9em;
    color: var(--text-color-muted);
    font-weight: 500;
}

.form-input {
    padding: 10px 12px;
    border: 1px solid var(--border-color);
    border-radius: 4px;
    background: var(--bg-color);
    color: var(--text-color);
    font-size: 0.9em;
}

.form-input:focus {
    outline: none;
    border-color: var(--color-primary);
    box-shadow: 0 0 0 2px rgba(var(--color-primary-rgb), 0.2);
}

.form-textarea {
    padding: 10px 12px;
    border: 1px solid var(--border-color);
    border-radius: 4px;
    background: var(--bg-color);
    color: var(--text-color);
    font-size: 0.9em;
    min-height: 80px;
    resize: vertical;
}

/* Trading History */
.trading-history {
    background: var(--message-bg-color);
    border-radius: 8px;
    padding: 20px;
    border: 1px solid var(--border-color);
}

.history-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 15px;
}

.history-table th,
.history-table td {
    padding: 12px;
    text-align: left;
    border-bottom: 1px solid var(--border-color);
}

.history-table th {
    background: var(--bg-color);
    font-weight: 600;
    color: var(--text-color-muted);
    font-size: 0.9em;
}

.history-table td {
    color: var(--text-color);
    font-size: 0.9em;
}

.history-table tbody tr:hover {
    background: var(--bg-color);
}

/* Trading Tabs */
.trading-tabs {
    display: flex;
    border-bottom: 1px solid var(--border-color);
    margin-bottom: 20px;
}

.trading-tab {
    padding: 12px 20px;
    background: transparent;
    border: none;
    color: var(--text-color-muted);
    cursor: pointer;
    font-size: 0.9em;
    font-weight: 500;
    transition: all 0.3s ease;
    border-bottom: 2px solid transparent;
}

.trading-tab.active {
    color: var(--color-primary);
    border-bottom-color: var(--color-primary);
}

.trading-tab:hover {
    color: var(--color-primary);
    background: rgba(var(--color-primary-rgb), 0.1);
}

.trading-tab-content {
    display: none;
}

.trading-tab-content.active {
    display: block;
}

/* Quick Actions */
.quick-actions {
    display: flex;
    gap: 10px;
    margin-bottom: 20px;
    flex-wrap: wrap;
}

.quick-action {
    padding: 8px 16px;
    background: var(--color-primary);
    color: white;
    border: none;
    border-radius: 20px;
    font-size: 0.8em;
    cursor: pointer;
    transition: all 0.3s ease;
}

.quick-action:hover {
    background: var(--color-primary-dark);
    transform: scale(1.05);
}

/* Loading States */
.loading-spinner {
    display: inline-block;
    width: 20px;
    height: 20px;
    border: 2px solid transparent;
    border-top: 2px solid var(--color-primary);
    border-radius: 50%;
    animation: spin 1s linear infinite;
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

.loading-overlay {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(0, 0, 0, 0.1);
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 8px;
