}

[data-theme="dark"] .trading-card:hover {
    box-shadow: 0 4px 16px rgba(255, 255, 255, 0.1);
}

[data-theme="dark"] .history-table tbody tr:hover {
    background: rgba(255, 255, 255, 0.05);
}

