"""
AutoGen Integration Helper for Agent Zero Trading System
Provides multi-agent coordination and specialized trading teams
"""

import asyncio
import json
import logging
from typing import Dict, List, Optional, Any
from datetime import datetime

try:
    from autogen_agentchat.agents import AssistantAgent
    from autogen_agentchat.teams import RoundRobinGroupChat
    from autogen_agentchat.messages import TextMessage
    from autogen_ext.models import OpenAIChatCompletionClient
    AUTOGEN_AVAILABLE = True
except ImportError:
    AUTOGEN_AVAILABLE = False
    logging.warning("AutoGen not available. Trading agent teams will use fallback mode.")

from python.helpers.print_style import PrintStyle

class AutoGenIntegration:
    """AutoGen integration for multi-agent trading coordination"""

    def __init__(self, config: Dict[str, Any] = None):
        self.config = config or {}
        self.agents = {}
        self.teams = {}
        self.client = None
        self.logger = logging.getLogger(__name__)

        if AUTOGEN_AVAILABLE and self.config.get('openai_api_key'):
            self._initialize_client()

    def _initialize_client(self):
        """Initialize OpenAI client for AutoGen"""
        try:
            self.client = OpenAIChatCompletionClient(
                model="gpt-4o-mini",
                api_key=self.config.get('openai_api_key')
            )
            PrintStyle(color="green").print("AutoGen client initialized successfully")
        except Exception as e:
            self.logger.error(f"Failed to initialize AutoGen client: {e}")

    def create_trading_agent(self, name: str, role: str, system_message: str) -> Dict[str, Any]:
        """Create a specialized trading agent"""

        if not AUTOGEN_AVAILABLE or not self.client:
            # Fallback mode - return agent config for manual processing
            agent_config = {
                'name': name,
                'role': role,
                'system_message': system_message,
                'created_at': datetime.now().isoformat(),
                'type': 'fallback',
                'status': 'active'
            }
            self.agents[name] = agent_config
            return agent_config

        try:
            agent = AssistantAgent(
                name=name,
                model_client=self.client,
                system_message=system_message
            )

            agent_config = {
                'name': name,
                'role': role,
                'system_message': system_message,
                'agent': agent,
                'created_at': datetime.now().isoformat(),
                'type': 'autogen',
                'status': 'active'
            }

            self.agents[name] = agent_config
            PrintStyle(color="cyan").print(f"Created AutoGen trading agent: {name} ({role})")
            return agent_config

        except Exception as e:
            self.logger.error(f"Failed to create AutoGen agent {name}: {e}")
            return self.create_trading_agent(name, role, system_message)  # Fallback

    def create_market_analyst_agent(self) -> Dict[str, Any]:
        """Create a market analysis specialist agent"""
        system_message = """You are a Market Analyst Agent specializing in cryptocurrency and DeFi markets.

        Your responsibilities:
        - Analyze market trends and patterns
        - Provide technical analysis insights
        - Monitor trading volumes and liquidity
        - Identify potential trading opportunities
        - Assess market sentiment and news impact

        Always provide data-driven analysis with specific recommendations for trading strategies."""

        return self.create_trading_agent("MarketAnalyst", "market_analysis", system_message)

    def create_risk_manager_agent(self) -> Dict[str, Any]:
        """Create a risk management specialist agent"""
        system_message = """You are a Risk Manager Agent focused on protecting capital and managing trading risks.

        Your responsibilities:
        - Calculate position sizes based on risk tolerance
        - Monitor portfolio exposure and concentration
        - Set and adjust stop-loss levels
        - Evaluate correlation risks across positions
        - Enforce risk management rules and limits

        Always prioritize capital preservation and provide clear risk assessments."""

        return self.create_trading_agent("RiskManager", "risk_management", system_message)

    def create_execution_agent(self) -> Dict[str, Any]:
        """Create a trade execution specialist agent"""
        system_message = """You are an Execution Agent responsible for optimal trade execution on Hyperliquid DEX.

        Your responsibilities:
        - Execute trades with optimal timing and sizing
        - Monitor slippage and market impact
        - Manage order types and execution strategies
        - Track trade performance and execution quality
        - Coordinate with other agents for trade decisions

        Focus on minimizing costs and maximizing execution efficiency."""

        return self.create_trading_agent("ExecutionAgent", "trade_execution", system_message)

    def create_trading_team(self, team_name: str, agents: List[str]) -> Dict[str, Any]:
        """Create a coordinated trading team from multiple agents"""

        team_agents = []
        agent_configs = []

        for agent_name in agents:
            if agent_name in self.agents:
                agent_config = self.agents[agent_name]
                agent_configs.append(agent_config)

                if agent_config.get('type') == 'autogen' and agent_config.get('agent'):
                    team_agents.append(agent_config['agent'])

        if not team_agents and AUTOGEN_AVAILABLE:
            # Create default team agents if none exist
            self.create_market_analyst_agent()
            self.create_risk_manager_agent()
            self.create_execution_agent()

            for agent_name in ['MarketAnalyst', 'RiskManager', 'ExecutionAgent']:
                if agent_name in self.agents and self.agents[agent_name].get('agent'):
                    team_agents.append(self.agents[agent_name]['agent'])
                    agent_configs.append(self.agents[agent_name])

        team_config = {
            'name': team_name,
            'agents': agent_configs,
            'created_at': datetime.now().isoformat(),
            'status': 'active',
            'type': 'autogen' if team_agents else 'fallback'
        }

        if team_agents and AUTOGEN_AVAILABLE:
            try:
                team_chat = RoundRobinGroupChat(team_agents)
                team_config['chat'] = team_chat
                PrintStyle(color="green").print(f"Created AutoGen trading team: {team_name}")
            except Exception as e:
                self.logger.error(f"Failed to create AutoGen team {team_name}: {e}")
                team_config['type'] = 'fallback'

        self.teams[team_name] = team_config
        return team_config

    async def run_team_analysis(self, team_name: str, task: str) -> Dict[str, Any]:
        """Run a coordinated analysis task with a trading team"""

        if team_name not in self.teams:
            return {'error': f'Team {team_name} not found'}

        team = self.teams[team_name]

        if team.get('type') == 'autogen' and team.get('chat'):
            try:
                # Run AutoGen team chat
                result = await team['chat'].run(task=task)

                return {
                    'team': team_name,
                    'task': task,
                    'result': result,
                    'timestamp': datetime.now().isoformat(),
                    'type': 'autogen'
                }

            except Exception as e:
                self.logger.error(f"AutoGen team analysis failed: {e}")
                # Fallback to manual coordination

        # Fallback mode - simulate team coordination
        responses = {}
        for agent_config in team.get('agents', []):
            agent_name = agent_config['name']
            role = agent_config['role']

            # Simulate agent response based on role
            if role == 'market_analysis':
                responses[agent_name] = f"Market analysis for task: {task}"
            elif role == 'risk_management':
                responses[agent_name] = f"Risk assessment for task: {task}"
            elif role == 'trade_execution':
                responses[agent_name] = f"Execution plan for task: {task}"
            else:
                responses[agent_name] = f"Analysis from {agent_name}: {task}"

        return {
            'team': team_name,
            'task': task,
            'responses': responses,
            'timestamp': datetime.now().isoformat(),
            'type': 'fallback'
        }

    def get_agent_status(self, agent_name: str) -> Dict[str, Any]:
        """Get status of a specific agent"""
        if agent_name in self.agents:
            return self.agents[agent_name]
        return {'error': 'Agent not found'}

    def get_team_status(self, team_name: str) -> Dict[str, Any]:
        """Get status of a specific team"""
        if team_name in self.teams:
            return self.teams[team_name]
        return {'error': 'Team not found'}

    def list_agents(self) -> List[Dict[str, Any]]:
        """List all created agents"""
        return list(self.agents.values())

    def list_teams(self) -> List[Dict[str, Any]]:
        """List all created teams"""
        return list(self.teams.values())

    def is_available(self) -> bool:
        """Check if AutoGen is available and configured"""
        return AUTOGEN_AVAILABLE and self.client is not None

# Global instance
autogen_integration = None

def get_autogen_integration(config: Dict[str, Any] = None) -> AutoGenIntegration:
    """Get or create AutoGen integration instance"""
    global autogen_integration
    if autogen_integration is None:
        autogen_integration = AutoGenIntegration(config)
    return autogen_integration

def create_default_trading_team(config: Dict[str, Any] = None) -> Dict[str, Any]:
    """Create a default trading team with market analyst, risk manager, and execution agent"""
    integration = get_autogen_integration(config)

    # Create individual agents
    integration.create_market_analyst_agent()
    integration.create_risk_manager_agent()
    integration.create_execution_agent()

    # Create coordinated team
    return integration.create_trading_team(
        "DefaultTradingTeam", 
        ["MarketAnalyst", "RiskManager", "ExecutionAgent"]
    )

