            # Fallback mode - use in-memory storage
            memory_config = {
                'agent_name': agent_name,
                'persona': persona or f"Trading agent {agent_name}",
                'human': human or "Trading system user",
                'created_at': datetime.now().isoformat(),
                'type': 'fallback',
                'memories': [],
                'conversations': []
            }
            self.memories[agent_name] = memory_config
            return memory_config

        try:
            # Create Letta agent with persistent memory
