    def store_trading_experience(self, agent_name: str, experience: Dict[str, Any]) -> bool:
        """Store a trading experience in agent memory"""

        if agent_name not in self.memories:
            self.create_agent_memory(agent_name)

        memory_config = self.memories[agent_name]

        if memory_config.get('type') == 'letta' and self.client:
            try:
                agent_id = memory_config['agent_id']

