        """Recall similar trading experiences from memory"""

        if agent_name not in self.memories:
            return []

        memory_config = self.memories[agent_name]

        if memory_config.get('type') == 'letta' and self.client:
            try:
                agent_id = memory_config['agent_id']

                # Query Letta memory for similar experiences
                search_query = f"Recall similar trading experiences related to: {query}"
                response = self.client.user_message(agent_id=agent_id, message=search_query)

                # Parse response and return structured data
                return [{
                    'content': response.messages[-1].text if response.messages else "No similar experiences found",
                    'source': 'letta_memory',
                    'timestamp': datetime.now().isoformat()
                }]

            except Exception as e:
                self.logger.error(f"Failed to recall Letta memory for {agent_name}: {e}")

        # Fallback mode - simple keyword search
        experiences = memory_config.get('memories', [])
        similar = []

        query_lower = query.lower()
        for exp in experiences:
            score = 0

            # Simple relevance scoring
            for key, value in exp.items():
                if isinstance(value, str) and query_lower in value.lower():
                    score += 1

            if score > 0:
                exp_copy = exp.copy()
                exp_copy['relevance_score'] = score
                similar.append(exp_copy)

        # Sort by relevance and return top results
        similar.sort(key=lambda x: x.get('relevance_score', 0), reverse=True)
        return similar[:limit]

