        """Get insights and learned patterns from agent memory"""

        if agent_name not in self.memories:
            return {'insights': 'No memory found for agent'}

        memory_config = self.memories[agent_name]

        if memory_config.get('type') == 'letta' and self.client:
            try:
                agent_id = memory_config['agent_id']

                # Request insights from Letta agent
                insights_query = "Summarize your key trading insights and learned patterns. What strategies work best in different market conditions?"
                response = self.client.user_message(agent_id=agent_id, message=insights_query)

                return {
