        # Simple analysis
        total_trades = len(experiences)
        profitable_trades = len([exp for exp in experiences if exp.get('pnl', 0) > 0])
        win_rate = (profitable_trades / total_trades * 100) if total_trades > 0 else 0

        strategies = {}
        for exp in experiences:
            strategy = exp.get('strategy', 'Unknown')
            if strategy not in strategies:
                strategies[strategy] = {'count': 0, 'total_pnl': 0}
            strategies[strategy]['count'] += 1
            strategies[strategy]['total_pnl'] += exp.get('pnl', 0)

        best_strategy = max(strategies.items(), key=lambda x: x[1]['total_pnl']) if strategies else None

        insights = f"""Trading Performance Analysis:
        Total Trades: {total_trades}
        Win Rate: {win_rate:.2f}%
        Most Used Strategies: {', '.join(list(strategies.keys())[:3])}
        Best Performing Strategy: {best_strategy[0] if best_strategy else 'Unknown'}
        """

        return {
            'insights': insights,
            'statistics': {
                'total_trades': total_trades,
                'win_rate': win_rate,
                'strategies': strategies
            },
            'source': 'fallback_analysis',
            'generated_at': datetime.now().isoformat()
        }

