                self.client.update_agent(agent_id=agent_id, persona=new_persona)
                memory_config['persona'] = new_persona

                PrintStyle(color="green").print(f"Updated persona for {agent_name}")
                return True

            except Exception as e:
                self.logger.error(f"Failed to update Letta persona for {agent_name}: {e}")

        # Fallback mode
        memory_config['persona'] = new_persona
        memory_config['updated_at'] = datetime.now().isoformat()
        return True

    def get_memory_status(self, agent_name: str) -> Dict[str, Any]:
        """Get memory status for an agent"""
