        return {'error': 'No memory found for agent'}

    def list_agent_memories(self) -> List[str]:
        """List all agents with memory"""
        return list(self.memories.keys())

    def is_available(self) -> bool:
        """Check if Letta is available and configured"""
        return LETTA_AVAILABLE and self.client is not None

# Global instance
letta_memory = None

def get_letta_memory(config: Dict[str, Any] = None) -> LettaMemoryIntegration:
    """Get or create Letta memory integration instance"""
    global letta_memory
    if letta_memory is None:
        letta_memory = LettaMemoryIntegration(config)
    return letta_memory

def store_trading_outcome(agent_name: str, trade_data: Dict[str, Any], config: Dict[str, Any] = None) -> bool:
