"""
Redis Integration for Agent Zero Trading System
Provides high-speed caching and real-time data management
"""

import asyncio
import json
import logging
from typing import Dict, List, Optional, Any, Union
from datetime import datetime, timedelta
import pickle

try:
    import redis
    import redis.asyncio as aioredis
    REDIS_AVAILABLE = True
except ImportError:
    REDIS_AVAILABLE = False
    logging.warning("Redis not available. Caching will use fallback mode.")

from python.helpers.print_style import PrintStyle

class RedisIntegration:
    """Redis integration for caching and real-time data"""

    def __init__(self, config: Dict[str, Any] = None):
        self.config = config or {}
        self.client = None
        self.async_client = None
        self.cache = {}  # Fallback in-memory cache
        self.logger = logging.getLogger(__name__)

        if REDIS_AVAILABLE:
            self._initialize_clients()

    def _initialize_clients(self):
        """Initialize Redis clients"""
        try:
            redis_url = self.config.get('redis_url', 'redis://localhost:6379')

            # Synchronous client
            self.client = redis.from_url(redis_url, decode_responses=True)
            self.client.ping()  # Test connection

            # Asynchronous client
            self.async_client = aioredis.from_url(redis_url, decode_responses=True)

            PrintStyle(color="green").print("Redis clients initialized successfully")

        except Exception as e:
            self.logger.error(f"Failed to initialize Redis clients: {e}")
            self.client = None
            self.async_client = None

    def set_cache(self, key: str, value: Any, ttl: int = 3600) -> bool:
        """Set a value in cache with TTL"""

        if self.client:
            try:
                # Serialize complex objects
                if isinstance(value, (dict, list)):
                    serialized_value = json.dumps(value)
                else:
                    serialized_value = str(value)

                self.client.setex(key, ttl, serialized_value)
                return True

            except Exception as e:
                self.logger.error(f"Redis set failed: {e}")

        # Fallback to in-memory cache
        self.cache[key] = {
            'value': value,
            'expires_at': datetime.now() + timedelta(seconds=ttl)
        }
        return True

    def get_cache(self, key: str) -> Optional[Any]:
        """Get a value from cache"""

        if self.client:
            try:
                value = self.client.get(key)
                if value:
                    # Try to deserialize JSON
                    try:
                        return json.loads(value)
                    except json.JSONDecodeError:
                        return value
                return None

            except Exception as e:
                self.logger.error(f"Redis get failed: {e}")

        # Fallback to in-memory cache
        if key in self.cache:
            cached = self.cache[key]
            if datetime.now() < cached['expires_at']:
                return cached['value']
            else:
                del self.cache[key]  # Remove expired entry

        return None

    def delete_cache(self, key: str) -> bool:
