        # Fallback stats
        return {
            'type': 'fallback',
            'cache_entries': len(self.cache),
            'memory_usage': 'Unknown'
        }

    def is_available(self) -> bool:
        """Check if Redis is available and connected"""
        if self.client:
            try:
                self.client.ping()
                return True
            except:
                return False
        return False

# Global instance
redis_integration = None

def get_redis_integration(config: Dict[str, Any] = None) -> RedisIntegration:
    """Get or create Redis integration instance"""
    global redis_integration
    if redis_integration is None:
