"""
Hyperliquid DEX Trading Integration for Agent Zero
Provides live trading capabilities with risk management
"""

import asyncio
import json
import logging
from typing import Dict, List, Optional, Any, Union
from datetime import datetime
import uuid

try:
    from hyperliquid.info import Info
    from hyperliquid.exchange import Exchange
    from hyperliquid.utils import constants
    HYPERLIQUID_AVAILABLE = True
except ImportError:
    HYPERLIQUID_AVAILABLE = False
    logging.warning("Hyperliquid SDK not available. Trading will use simulation mode.")

from python.helpers.tool import Tool, Response
from python.helpers.print_style import PrintStyle

class HyperliquidTrading(Tool):
    """Hyperliquid DEX trading tool"""

    def __init__(self, agent):
        super().__init__(agent)
        self.info = None
        self.exchange = None
        self.wallet_address = None
        self.private_key = None
        self.is_testnet = True  # Default to testnet for safety
        self.simulation_mode = not HYPERLIQUID_AVAILABLE
        self.position_size_limit = 1000  # Max position size in USD
        self.daily_loss_limit = 50  # Max daily loss in USD

        self._initialize_client()

    def _initialize_client(self):
        """Initialize Hyperliquid clients"""

        if not HYPERLIQUID_AVAILABLE:
            PrintStyle(color="yellow").print("Hyperliquid SDK not available - using simulation mode")
            return

        try:
            # Get configuration from agent settings
            config = getattr(self.agent, 'config', {})
            self.private_key = config.get('hyperliquid_private_key', '0xb43657ecf409688498b14475a06cd96f18d460ce994f7022055b51e5c8f29d84')
            self.wallet_address = config.get('hyperliquid_wallet_address', '0x9e9c586318F8508a92f9c5A500c23bAAB329C22d')
            self.is_testnet = config.get('hyperliquid_testnet', True)

            if self.private_key and self.wallet_address:
                # Initialize Info client for market data
                base_url = constants.TESTNET_API_URL if self.is_testnet else constants.MAINNET_API_URL
                self.info = Info(base_url, skip_ws=True)

                # Initialize Exchange client for trading
                self.exchange = Exchange(
                    self.wallet_address,
                    self.private_key,
                    base_url=base_url,
                    skip_ws=True
                )

                PrintStyle(color="green").print(f"Hyperliquid client initialized - {'Testnet' if self.is_testnet else 'Mainnet'}")
            else:
                PrintStyle(color="yellow").print("Hyperliquid credentials not provided - using simulation mode")
                self.simulation_mode = True

        except Exception as e:
            logging.error(f"Failed to initialize Hyperliquid client: {e}")
            self.simulation_mode = True

    def execute(self, **kwargs) -> Response:
        """Execute trading operations"""

        action = kwargs.get('action', 'get_balance')

        try:
            if action == 'get_balance':
                return self._get_balance()
            elif action == 'get_positions':
                return self._get_positions()
            elif action == 'get_market_data':
                symbol = kwargs.get('symbol', 'ETH')
                return self._get_market_data(symbol)
            elif action == 'place_order':
                return self._place_order(kwargs)
            elif action == 'cancel_order':
                order_id = kwargs.get('order_id')
                return self._cancel_order(order_id)
            elif action == 'close_position':
                symbol = kwargs.get('symbol')
                return self._close_position(symbol)
            elif action == 'get_trading_stats':
                return self._get_trading_stats()
            else:
                return Response(
                    message=f"Unknown action: {action}",
                    data={'error': 'Invalid action'}
                )

        except Exception as e:
            logging.error(f"Hyperliquid trading error: {e}")
            return Response(
                message=f"Trading operation failed: {str(e)}",
                data={'error': str(e)}
            )

    def _get_balance(self) -> Response:
        """Get account balance"""

        if self.simulation_mode:
            return Response(
                message="Simulation Mode - Account Balance",
                data={
                    'balance': 10000.0,
                    'available': 8500.0,
                    'margin_used': 1500.0,
                    'mode': 'simulation'
                }
            )

        try:
            user_state = self.info.user_state(self.wallet_address)
            balance_data = {
                'balance': float(user_state.get('marginSummary', {}).get('accountValue', 0)),
                'available': float(user_state.get('marginSummary', {}).get('totalMarginUsed', 0)),
                'margin_used': float(user_state.get('marginSummary', {}).get('totalMarginUsed', 0)),
                'mode': 'live'
            }

            return Response(
                message=f"Account Balance: ${balance_data['balance']:.2f}",
                data=balance_data
            )

        except Exception as e:
