                    })

            return Response(
                message=f"Found {len(position_data)} open positions",
                data={
                    'positions': position_data,
                    'mode': 'live'
                }
            )

        except Exception as e:
            return Response(
                message=f"Failed to get positions: {str(e)}",
                data={'error': str(e)}
            )

