        if agent_name != 'system':
            self.redis.increment_trade_counter(agent_name)

        # Update goal progress if applicable
        if goal_id:
            self._update_goal_progress(goal_id, trade_record)

        PrintStyle(color=\"green\").print(f\"Trade executed: {side} {size} {symbol} by {agent_name}\")

        return Response(
            message=f\"Trade executed successfully: {side.upper()} {size} {symbol}\",
            data=trade_record
        )

    def _update_goal_progress(self, goal_id: str, trade_record: Dict[str, Any]) -> None:
        \"\"\"Update goal progress after a trade\"\"\"

        # Get current progress
        progress = self.redis.get_goal_progress(goal_id) or {
            'current_trades': 0,
            'current_profit': 0.0,
            'progress_percentage': 0.0
        }

        # Update counters
        progress['current_trades'] = progress.get('current_trades', 0) + 1

        # Calculate progress percentage
        if goal_id in self.active_goals:
            goal = self.active_goals[goal_id]
            target_trades = goal.get('target_trades_per_day', 200)
            progress['progress_percentage'] = min(100, (progress['current_trades'] / target_trades) * 100)

        # Store updated progress
        self.redis.store_goal_progress(goal_id, progress)

    def _get_goal_progress(self, params: Dict[str, Any]) -> Response:
        \"\"\"Get progress for a specific goal\"\"\"

        goal_id = params.get('goal_id')

        if not goal_id:
            return Response(
                message=\"Goal ID required\",
                data={'error': 'Missing goal_id'}
            )

        # Get progress from Redis
        progress = self.redis.get_goal_progress(goal_id)

        if not progress:
            return Response(
                message=\"Goal progress not found\",
                data={'error': 'Goal not found'}
            )

        # Get goal details
        goal = self.active_goals.get(goal_id, {})

        result = {
            'goal_id': goal_id,
            'goal_name': goal.get('name', 'Unknown'),
            'target_trades_per_day': goal.get('target_trades_per_day', 0),
            'target_profit_per_day': goal.get('target_profit_per_day', 0),
            'current_trades': progress.get('current_trades', 0),
            'current_profit': progress.get('current_profit', 0.0),
            'progress_percentage': progress.get('progress_percentage', 0.0),
            'assigned_agents': goal.get('assigned_agents', [])
        }

        return Response(
            message=f\"Goal progress: {result['progress_percentage']:.1f}% complete\",
            data=result
        )

    def _get_agent_insights(self, params: Dict[str, Any]) -> Response:
        \"\"\"Get insights from an agent's memory\"\"\"

        agent_name = params.get('agent_name')

        if not agent_name:
            return Response(
                message=\"Agent name required\",
                data={'error': 'Missing agent_name'}
            )

        # Get insights from Letta memory
        insights = get_trading_insights(agent_name, self.config)

        return Response(
            message=f\"Trading insights for {agent_name}\",
            data=insights
        )

    def _list_goals(self) -> Response:
        \"\"\"List all trading goals\"\"\"

        goals = list(self.active_goals.values())

        return Response(
            message=f\"Found {len(goals)} active goals\",
            data={'goals': goals}
        )

    def _list_agents(self) -> Response:
        \"\"\"List all trading agents\"\"\"

        agents = [agent['record'] for agent in self.trading_agents.values()]

        return Response(
            message=f\"Found {len(agents)} trading agents\",
            data={'agents': agents}
        )

    def _get_system_status(self) -> Response:
        \"\"\"Get overall system status\"\"\"

        status = {
            'integrations': {
                'autogen': self.autogen.is_available(),
                'letta': self.letta.is_available(),
                'redis': self.redis.is_available(),
                'supabase': self.supabase.is_available(),
                'hyperliquid': not self.hyperliquid.simulation_mode
            },
            'active_goals': len(self.active_goals),
            'trading_agents': len(self.trading_agents),
            'system_time': datetime.now().isoformat()
        }

        return Response(
            message=\"Trading system status retrieved\",
            data=status
        )

