input.content:
/* Trading Dashboard Styles */

.trading-container {
    padding: 15px;
    background: var(--color-primary-very-dark);
    border-radius: 8px;
    margin-bottom: 15px;
}

.trading-quick-actions {
    display: flex;
    gap: 10px;
    margin-bottom: 20px;
    flex-wrap: wrap;
}

.trading-button {
    padding: 8px 16px;
    border: none;
    border-radius: 6px;
    cursor: pointer;
    font-size: 14px;
    font-weight: 500;
    display: flex;
    align-items: center;
    gap: 6px;
    transition: all 0.2s ease;
    text-decoration: none;
    color: white;
    background: var(--color-primary);
}

.trading-button:hover {
    transform: translateY(-1px);
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
}

.trading-button.primary {
    background: var(--color-accent);
}

.trading-button.success {
    background: #28a745;
}

.trading-button.warning {
    background: #ffc107;
    color: #000;
}

.trading-button.danger {
    background: #dc3545;
}

.trading-button.secondary {
    background: #6c757d;
}

.trading-summary {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
    gap: 15px;
    margin-bottom: 25px;
}

.summary-card {
    background: var(--color-primary-dark);
    padding: 15px;
    border-radius: 8px;
    text-align: center;
    border: 1px solid var(--color-border);
}

.summary-label {
    font-size: 12px;
    color: var(--color-text-secondary);
    text-transform: uppercase;
    letter-spacing: 0.5px;
    margin-bottom: 5px;
}

.summary-value {
    font-size: 18px;
    font-weight: 600;
    color: var(--color-text);
}

.summary-value.positive {
    color: #28a745;
}

.summary-value.negative {
    color: #dc3545;
}

.trading-agents-list {
    background: var(--color-primary-dark);
    padding: 15px;
    border-radius: 8px;
    border: 1px solid var(--color-border);
}

.trading-agents-list h4 {
    margin: 0 0 15px 0;
    color: var(--color-text);
    font-size: 16px;
    font-weight: 600;
}

.agent-item {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px;
    background: var(--color-primary-very-dark);
    border-radius: 6px;
    margin-bottom: 8px;
    border: 1px solid var(--color-border);
}

.agent-name {
    font-weight: 500;
    color: var(--color-text);
}

.agent-status {
    padding: 4px 8px;
    border-radius: 4px;
    font-size: 12px;
    font-weight: 500;
    text-transform: uppercase;
    background: var(--color-accent);
    color: white;
}

.agent-status.active {
    background: #28a745;
}

.agent-status.paused {
    background: #ffc107;
    color: #000;
}

.agent-status.error {
    background: #dc3545;
}

.empty-message {
    text-align: center;
    color: var(--color-text-secondary);
    font-style: italic;
    padding: 20px;
}

/* Trading Modals */
.trading-modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.7);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 1000;
}

.trading-modal-content {
    background: var(--color-primary-dark);
    padding: 30px;
    border-radius: 12px;
    max-width: 500px;
    width: 90%;
    max-height: 80vh;
    overflow-y: auto;
    border: 1px solid var(--color-border);
}

.trading-modal h3 {
    margin: 0 0 20px 0;
    color: var(--color-text);
    font-size: 20px;
    font-weight: 600;
}

.trading-form-group {
    margin-bottom: 20px;
}

.trading-form-group label {
    display: block;
    margin-bottom: 5px;
    color: var(--color-text);
    font-weight: 500;
}

.trading-form-group input,
.trading-form-group select,
.trading-form-group textarea {
    width: 100%;
    padding: 10px;
    border: 1px solid var(--color-border);
    border-radius: 6px;
    background: var(--color-primary-very-dark);
    color: var(--color-text);
    font-size: 14px;
}

.trading-form-group input:focus,
.trading-form-group select:focus,
.trading-form-group textarea:focus {
    outline: none;
    border-color: var(--color-accent);
    box-shadow: 0 0 0 2px rgba(var(--color-accent-rgb), 0.2);
}

.trading-form-actions {
    display: flex;
    gap: 10px;
    justify-content: flex-end;
    margin-top: 30px;
}

/* Trading Goals Section */
.trading-goals {
    margin-top: 25px;
}

.trading-goals h4 {
    margin: 0 0 15px 0;
    color: var(--color-text);
    font-size: 16px;
    font-weight: 600;
}

.goal-item {
    background: var(--color-primary-dark);
    padding: 15px;
    border-radius: 8px;
    margin-bottom: 10px;
    border: 1px solid var(--color-border);
}

.goal-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 10px;
}

.goal-name {
    font-weight: 600;
    color: var(--color-text);
}

.goal-status {
    padding: 4px 8px;
    border-radius: 4px;
    font-size: 12px;
    font-weight: 500;
    text-transform: uppercase;
    background: var(--color-accent);
    color: white;
}

.goal-progress {
    margin-bottom: 10px;
}

.progress-bar {
    width: 100%;
    height: 8px;
    background: var(--color-primary-very-dark);
    border-radius: 4px;
    overflow: hidden;
}

.progress-fill {
    height: 100%;
    background: var(--color-accent);
    transition: width 0.3s ease;
}

.goal-stats {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(120px, 1fr));
    gap: 10px;
    font-size: 12px;
    color: var(--color-text-secondary);
}

.goal-stat {
    text-align: center;
}

.goal-stat-value {
    font-weight: 600;
    color: var(--color-text);
    font-size: 14px;
}

/* Performance Charts */
.trading-charts {
    margin-top: 25px;
}

.chart-container {
    background: var(--color-primary-dark);
    padding: 20px;
    border-radius: 8px;
    border: 1px solid var(--color-border);
    height: 300px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: var(--color-text-secondary);
    font-style: italic;
}

/* Responsive Design */
@media (max-width: 768px) {
    .trading-quick-actions {
        flex-direction: column;
    }

    .trading-summary {
        grid-template-columns: 1fr;
    }

    .trading-modal-content {
        margin: 20px;
        padding: 20px;
    }

    .trading-form-actions {
        flex-direction: column;
    }

    .goal-stats {
        grid-template-columns: repeat(2, 1fr);
    }
}

/* Trading Tab Specific Styles */
#trading-tab.active {
    background: var(--color-accent);
    color: white;
}

#trading-section {
    padding-top: 10px;
}

/* Integration with existing Agent Zero styles */
.trading-container .config-button {
    background: var(--color-primary);
    border: 1px solid var(--color-border);
    color: var(--color-text);
    padding: 8px 16px;
    border-radius: 6px;
    cursor: pointer;
    transition: all 0.2s ease;
}

.trading-container .config-button:hover {
    background: var(--color-accent);
    transform: translateY(-1px);
}

/* Trading System Status */
.system-status {
    background: var(--color-primary-dark);
    padding: 15px;
    border-radius: 8px;
    border: 1px solid var(--color-border);
    margin-bottom: 20px;
}

.status-indicator {
    display: inline-block;
    width: 8px;
    height: 8px;
    border-radius: 50%;
    margin-right: 8px;
}

.status-indicator.connected {
    background: #28a745;
}

.status-indicator.disconnected {
    background: #dc3545;
}

.status-indicator.warning {
    background: #ffc107;
}

