 * Trading System JavaScript Module
 * Handles all trading-related UI interactions and API calls
 */

class TradingSystem {
    constructor() {
        this.apiBaseUrl = '/api';
        this.currentPortfolio = { total_value: 0, available_cash: 0, pnl: 0 };
        this.tradingAgents = [];
        this.goals = [];
        this.isInitialized = false;

        this.init();
    }

    async init() {
        console.log('Initializing Trading System...');

        // Set up tab switching
        this.setupTabSwitching();

        // Load initial data
        await this.loadSystemStatus();
        await this.loadPortfolio();
        await this.loadAgents();
        await this.loadGoals();

        this.isInitialized = true;
        console.log('Trading System initialized');
    }

    setupTabSwitching() {
        // Handle tab switching to show/hide trading section
        const tradingTab = document.getElementById('trading-tab');
        const tradingSection = document.getElementById('trading-section');
        const chatsSection = document.getElementById('chats-section');
        const tasksSection = document.getElementById('tasks-section');

        if (tradingTab) {
            tradingTab.addEventListener('click', () => {
                // Hide other sections
                if (chatsSection) chatsSection.style.display = 'none';
                if (tasksSection) tasksSection.style.display = 'none';

                // Show trading section
                if (tradingSection) tradingSection.style.display = 'block';

                // Update tab states
                document.querySelectorAll('.tab').forEach(tab => tab.classList.remove('active'));
                tradingTab.classList.add('active');

                // Refresh trading data when tab is opened
                this.refreshDashboard();
            });
        }
    }

    async apiCall(endpoint, method = 'GET', data = null) {
        try {
            const options = {
                method,
                headers: {
                    'Content-Type': 'application/json',
                }
            };

            if (data) {
                options.body = JSON.stringify(data);
            }

            const response = await fetch(`${this.apiBaseUrl}${endpoint}`, options);

            if (!response.ok) {
                throw new Error(`API call failed: ${response.status} ${response.statusText}`);
            }

            return await response.json();
        } catch (error) {
            console.error('API call error:', error);
            throw error;
        }
    }

    async loadSystemStatus() {
        try {
            // This would call the trading system status endpoint
            const response = await this.apiCall('/trading_system', 'POST', {
                action: 'status'
            });

            console.log('System status:', response);
        } catch (error) {
            console.error('Failed to load system status:', error);
        }
    }

    async loadPortfolio() {
        try {
            // Get portfolio data from Hyperliquid integration
            const response = await this.apiCall('/trading_system', 'POST', {
                action: 'get_trading_stats'
            });

            if (response.success && response.data) {
                this.currentPortfolio = {
                    total_value: response.data.account_balance || 0,
                    available_cash: response.data.available_balance || 0,
                    pnl: response.data.total_unrealized_pnl || 0
                };

                this.updatePortfolioDisplay();
            }
        } catch (error) {
            console.error('Failed to load portfolio:', error);
            // Use mock data for demo
            this.currentPortfolio = {
                total_value: 10000,
                available_cash: 8500,
                pnl: 150.75
            };
            this.updatePortfolioDisplay();
        }
    }

    async loadAgents() {
        try {
            const response = await this.apiCall('/trading_system', 'POST', {
                action: 'list_agents'
            });

            if (response.success && response.data) {
                this.tradingAgents = response.data.agents || [];
                this.updateAgentsDisplay();
            }
        } catch (error) {
            console.error('Failed to load agents:', error);
            // Use mock data for demo
            this.tradingAgents = [
                { name: 'ScalpingBot', status: 'active', specialization: 'scalping' },
                { name: 'SwingTrader', status: 'paused', specialization: 'swing' }
            ];
            this.updateAgentsDisplay();
        }
    }

    async loadGoals() {
        try {
            const response = await this.apiCall('/trading_system', 'POST', {
                action: 'list_goals'
            });

            if (response.success && response.data) {
                this.goals = response.data.goals || [];
                this.updateGoalsDisplay();
            }
        } catch (error) {
            console.error('Failed to load goals:', error);
            // Use mock data for demo
            this.goals = [
                {
                    id: '1',
                    name: 'Daily Profit Target',
                    target_trades_per_day: 200,
                    target_profit_per_day: 5.0,
                    current_trades: 45,
                    current_profit: 2.35,
                    progress_percentage: 47.0
                }
            ];
            this.updateGoalsDisplay();
        }
    }

    updatePortfolioDisplay() {
        // Update portfolio summary cards using Alpine.js data
        const tradingSection = document.querySelector('#trading-section');
        if (tradingSection && tradingSection.__x) {
            const alpineData = tradingSection.__x.$data;
            alpineData.portfolio = this.currentPortfolio;
        }
    }

    updateAgentsDisplay() {
        // Update agents list using Alpine.js data
        const tradingSection = document.querySelector('#trading-section');
        if (tradingSection && tradingSection.__x) {
            const alpineData = tradingSection.__x.$data;
            alpineData.tradingAgents = this.tradingAgents;
        }
    }

    updateGoalsDisplay() {
        // Add goals to the trading dashboard
        const dashboardContainer = document.getElementById('trading-dashboard');
        if (!dashboardContainer) return;

        // Remove existing goals section
        const existingGoals = dashboardContainer.querySelector('.trading-goals');
        if (existingGoals) {
            existingGoals.remove();
        }

        // Create goals section
        const goalsSection = document.createElement('div');
        goalsSection.className = 'trading-goals';
        goalsSection.innerHTML = `
            <h4>Trading Goals</h4>
            ${this.goals.length === 0 ? 
                '<div class="empty-message"><i>No trading goals created yet.</i></div>' :
                this.goals.map(goal => this.renderGoal(goal)).join('')
            }
            <button class="trading-button success" onclick="tradingSystem.createGoal()">
                <i class="fas fa-plus"></i> New Goal
            </button>
        `;

        dashboardContainer.appendChild(goalsSection);
    }

    renderGoal(goal) {
        return `
            <div class="goal-item">
                <div class="goal-header">
                    <span class="goal-name">${goal.name}</span>
                    <span class="goal-status">${goal.status || 'active'}</span>
                </div>
                <div class="goal-progress">
                    <div class="progress-bar">
                        <div class="progress-fill" style="width: ${goal.progress_percentage || 0}%"></div>
                    </div>
                </div>
                <div class="goal-stats">
                    <div class="goal-stat">
                        <div class="goal-stat-value">${goal.current_trades || 0}</div>
                        <div>Trades Today</div>
                    </div>
                    <div class="goal-stat">
                        <div class="goal-stat-value">$${(goal.current_profit || 0).toFixed(2)}</div>
                        <div>Profit Today</div>
                    </div>
                    <div class="goal-stat">
                        <div class="goal-stat-value">${goal.target_trades_per_day || 0}</div>
                        <div>Target Trades</div>
                    </div>
                    <div class="goal-stat">
                        <div class="goal-stat-value">$${(goal.target_profit_per_day || 0).toFixed(2)}</div>
                        <div>Target Profit</div>
                    </div>
                </div>
            </div>
        `;
    }

    async refreshDashboard() {
        console.log('Refreshing trading dashboard...');
        await Promise.all([
            this.loadPortfolio(),
            this.loadAgents(),
            this.loadGoals()
        ]);
    }

    async refreshPortfolio() {
        await this.loadPortfolio();
        this.showToast('Portfolio refreshed', 'success');
    }

    createAgent() {
        this.showAgentModal();
    }

    createGoal() {
        this.showGoalModal();
    }

    showAgentModal() {
        const modal = this.createModal('Create Trading Agent', `
            <form id="agent-form">
                <div class="trading-form-group">
                    <label for="agent-name">Agent Name</label>
                    <input type="text" id="agent-name" name="name" required placeholder="e.g., ScalpingBot">
                </div>
                <div class="trading-form-group">
                    <label for="agent-specialization">Specialization</label>
                    <select id="agent-specialization" name="specialization" required>
                        <option value="general">General Trading</option>
                        <option value="scalping">Scalping</option>
                        <option value="swing">Swing Trading</option>
                        <option value="arbitrage">Arbitrage</option>
                        <option value="market_analysis">Market Analysis</option>
                        <option value="risk_management">Risk Management</option>
                        <option value="execution">Trade Execution</option>
                    </select>
                </div>
                <div class="trading-form-group">
                    <label for="agent-description">Description (Optional)</label>
                    <textarea id="agent-description" name="description" rows="3" placeholder="Describe the agent's strategy and purpose..."></textarea>
                </div>
                <div class="trading-form-actions">
                    <button type="button" class="trading-button secondary" onclick="tradingSystem.closeModal()">Cancel</button>
                    <button type="submit" class="trading-button success">Create Agent</button>
                </div>
            </form>
        `);

        const form = modal.querySelector('#agent-form');
        form.addEventListener('submit', async (e) => {
            e.preventDefault();
            await this.handleCreateAgent(new FormData(form));
        });
    }

    showGoalModal() {
        const modal = this.createModal('Create Trading Goal', `
            <form id="goal-form">
                <div class="trading-form-group">
                    <label for="goal-name">Goal Name</label>
                    <input type="text" id="goal-name" name="name" required placeholder="e.g., Daily Profit Target">
                </div>
                <div class="trading-form-group">
                    <label for="goal-description">Description</label>
                    <textarea id="goal-description" name="description" rows="2" placeholder="Describe the trading goal..."></textarea>
                </div>
                <div class="trading-form-group">
                    <label for="target-trades">Target Trades per Day</label>
                    <input type="number" id="target-trades" name="target_trades_per_day" required value="200" min="1">
                </div>
                <div class="trading-form-group">
                    <label for="target-profit">Target Profit per Day ($)</label>
                    <input type="number" id="target-profit" name="target_profit_per_day" required value="5.0" min="0.01" step="0.01">
                </div>
                <div class="trading-form-actions">
                    <button type="button" class="trading-button secondary" onclick="tradingSystem.closeModal()">Cancel</button>
                    <button type="submit" class="trading-button success">Create Goal</button>
                </div>
            </form>
        `);

        const form = modal.querySelector('#goal-form');
        form.addEventListener('submit', async (e) => {
            e.preventDefault();
            await this.handleCreateGoal(new FormData(form));
        });
    }

    async handleCreateAgent(formData) {
        try {
            const agentData = {
                action: 'create_agent',
                name: formData.get('name'),
                specialization: formData.get('specialization'),
                description: formData.get('description')
            };

            const response = await this.apiCall('/trading_system', 'POST', agentData);

            if (response.success) {
                this.showToast(`Agent "${agentData.name}" created successfully`, 'success');
                this.closeModal();
                await this.loadAgents();
            } else {
                this.showToast('Failed to create agent', 'error');
            }
        } catch (error) {
            console.error('Error creating agent:', error);
            this.showToast('Error creating agent', 'error');
        }
    }

    async handleCreateGoal(formData) {
        try {
            const goalData = {
                action: 'create_goal',
                name: formData.get('name'),
                description: formData.get('description'),
                target_trades_per_day: parseInt(formData.get('target_trades_per_day')),
                target_profit_per_day: parseFloat(formData.get('target_profit_per_day'))
            };

            const response = await this.apiCall('/trading_system', 'POST', goalData);

            if (response.success) {
                this.showToast(`Goal "${goalData.name}" created successfully`, 'success');
                this.closeModal();
                await this.loadGoals();
            } else {
                this.showToast('Failed to create goal', 'error');
            }
        } catch (error) {
            console.error('Error creating goal:', error);
            this.showToast('Error creating goal', 'error');
        }
    }

    async runAnalysis() {
        try {
            this.showToast('Running market analysis...', 'info');

            const response = await this.apiCall('/trading_system', 'POST', {
                action: 'run_analysis'
            });

            if (response.success) {
                this.showToast('Analysis completed', 'success');
            } else {
                this.showToast('Analysis failed', 'error');
            }
        } catch (error) {
            console.error('Error running analysis:', error);
            this.showToast('Error running analysis', 'error');
        }
    }

    createModal(title, content) {
        // Remove existing modal
        this.closeModal();

        const modal = document.createElement('div');
        modal.className = 'trading-modal';
        modal.id = 'trading-modal';
        modal.innerHTML = `
            <div class="trading-modal-content">
                <h3>${title}</h3>
                ${content}
            </div>
        `;

        // Close modal on backdrop click
        modal.addEventListener('click', (e) => {
            if (e.target === modal) {
                this.closeModal();
            }
        });

        document.body.appendChild(modal);
        return modal;
    }

    closeModal() {
        const modal = document.getElementById('trading-modal');
        if (modal) {
            modal.remove();
        }
    }

    showToast(message, type = 'info') {
        // Use existing toast system if available, otherwise create simple notification
        if (window.showToast) {
            window.showToast(message, type);
        } else {
            console.log(`[${type.toUpperCase()}] ${message}`);

            // Create simple toast notification
            const toast = document.createElement('div');
            toast.style.cssText = `
                position: fixed;
                top: 20px;
                right: 20px;
                background: ${type === 'success' ? '#28a745' : type === 'error' ? '#dc3545' : '#007bff'};
                color: white;
                padding: 12px 20px;
                border-radius: 6px;
                z-index: 10000;
                font-size: 14px;
                box-shadow: 0 4px 8px rgba(0,0,0,0.2);
            `;
            toast.textContent = message;

            document.body.appendChild(toast);

            setTimeout(() => {
                if (toast.parentNode) {
                    toast.parentNode.removeChild(toast);
                }
            }, 3000);
        }
    }
}

// Initialize trading system when page loads
let tradingSystem;

if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', () => {
        tradingSystem = new TradingSystem();
    });
} else {
    tradingSystem = new TradingSystem();
}

// Export for global access
window.tradingSystem = tradingSystem;
